public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportLinesToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(tableStr(SalesAgreementHeaderEntity));

            var headerEntityInfo = context.findEntityByName(tableStr(SalesAgreementHeaderEntity));
            headerEntityInfo.filter(this.createFilterWithCompany(headerEntityInfo.entityName(), fieldStr(SalesAgreementHeaderEntity, SalesAgreementId), SalesAgreementHeader.SalesNumberSequence));

            var lineEntityInfo = context.addEntityDefault(tableStr(SalesAgreementLineEntity));
            lineEntityInfo.filter(this.createFilterWithCompany(lineEntityInfo.entityName(), fieldStr(SalesAgreementLineEntity, SalesAgreementId), SalesAgreementHeader.SalesNumberSequence));
            break;

        case ExportLineFulfillmentsToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(tableStr(SalesAgreementLineFulfillmentEntity));

            var fulfillmentEntityInfo = context.findEntityByName(tableStr(SalesAgreementLineFulfillmentEntity));
            fulfillmentEntityInfo.filter(this.createFilterWithCompany(fulfillmentEntityInfo.entityName(), fieldStr(SalesAgreementLineFulfillmentEntity, SalesAgreementId), SalesAgreementHeader.SalesNumberSequence));
            break;

        case ExportLineNotesToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(tableStr(SalesAgreementLineNoteEntity));

            var noteEntityInfo = context.findEntityByName(tableStr(SalesAgreementLineNoteEntity));
            noteEntityInfo.filter(this.createFilterWithCompany(noteEntityInfo.entityName(), fieldStr(SalesAgreementLineNoteEntity, SalesAgreementId), SalesAgreementHeader.SalesNumberSequence));
            break;
    }

    return context;
}

