/// <summary>
/// Creates a record in the specific data source.
/// </summary>
/// <param name="_formDataSourceName">
/// Name of the data source where the record will be created.
/// </param>
/// <param name="_append">
/// A Boolean that indicates whether to insert the record after or before the current
/// cursor position. If true, the new record is inserted after the current record; otherwise
/// before the current record.
/// </param>
/// <remarks>
/// If the current data source is the agreement line super type the correct sub types is created.
/// </remarks>
public void createRecord(str        _formDataSourceName,
                         boolean    _append = true)
{
    Map     tableNamesMap;

    if (_formDataSourceName == aLine_ds.name())
    {
        tableNamesMap = new Map(Types::String, Types::String);

        if (salesAgreementHeader.DefaultAgreementLineType == CommitmentType::ProductQuantity)
        {
            tableNamesMap.insert(_formDataSourceName, tableStr(AgreementLineQuantityCommitment));
        }
        else
        {
            tableNamesMap.insert(_formDataSourceName, tableStr(AgreementLineVolumeCommitment));
        }

        aLine_ds.createTypes(tableNamesMap, _append);
    }
    else
    {
        super(_formDataSourceName, _append);
    }
}

