/// <summary>
/// Disables fields that should not be edited once Route Plan has been selected.
/// </summary>
public void enableTMSFields()
{
    TMSTransportationTemplateId transTempId;
    TMSCarrierCode              carrierCode;
    TMSCarrierGroupCode         carrierGroupCode;
    TMSModeCode                 modeCode;
    TMSRouteConfigCode          routeConfigCode;
    boolean                     modeCodeDisabled;

    transTempId         = TMSSalesAgreementHeader.TransportationTemplateId;
    carrierCode         = TMSSalesAgreementHeader.CarrierCode;
    carrierGroupCode    = TMSSalesAgreementHeader.CarrierGroupCode;
    modeCode            = TMSSalesAgreementHeader.ModeCode;
    modeCodeDisabled    = TMSSalesAgreementHeader.ModeCode != '' && TMSSalesAgreementHeader.CarrierCode == '';
    routeConfigCode     = TMSSalesAgreementHeader.RouteConfigCode;

    tmsSalesAgreementHeader_ds.object(fieldNum(TMSSalesAgreementHeader, ModeCode)).allowEdit(!(carrierCode || transTempId || carrierGroupCode || routeConfigCode));
    tmsSalesAgreementHeader_ds.object(fieldNum(TMSSalesAgreementHeader, CarrierCode)).allowEdit(!(transTempId ||carrierGroupCode || modeCodeDisabled || routeConfigCode));
    tmsSalesAgreementHeader_ds.object(fieldNum(TMSSalesAgreementHeader, CarrierServiceCode)).allowEdit(!(transTempId || carrierGroupCode || modeCodeDisabled || routeConfigCode));
    tmsSalesAgreementHeader_ds.object(fieldNum(TMSSalesAgreementHeader, CarrierGroupCode)).allowEdit(!(transTempId || carrierCode || routeConfigCode || modeCode));
    tmsSalesAgreementHeader_ds.object(fieldNum(TMSSalesAgreementHeader, RouteConfigCode)).allowEdit(!(carrierCode || transTempId || modeCode || carrierGroupCode));
    tmsSalesAgreementHeader_ds.object(fieldNum(TMSSalesAgreementHeader, TransportationTemplateId)).allowEdit(!(carrierCode || carrierGroupCode || modeCode || routeConfigCode));
}

