/// <summary>
/// Initializes the Sales Agreement form and creates a new instance of the Sales Agreement Form class.
/// </summary>
public void init()
{
    custParameters = CustParameters::find();

    isCalledFromLP = TradeFormHelper::isCalledFromForm(element.args(), formStr(SalesAgreementListPage));

    if (element.args() && !isCalledFromLP)
    {
        element.args().copyCallerQuery(CopyCallerQuery::No);
    }

    super();

    element.buildSharedDatasourceManager();

    // Init of dependency objects.
    agreementClassificationLookup = AgreementClassificationLookup::newAgreementClassificationLookup(tablenum(SalesAgreementHeader));

    salesAgreementForm = SalesAgreementForm::newFormRun(element);
    salesAgreementForm.getSysLastValue();
    salesAgreementForm.parmHeaderDefaultDimensionEntryControl(DimensionEntryControlHeader);

    salesAgreementForm.initRangesFromCaller(salesAgreementHeader_ds, agreementHeaderDefault_ds);

    salesAgreementFormArgs = AgreementFormArgs::construct();
    salesAgreementForm.parmSalesAgreementFormArgs(salesAgreementFormArgs);

    if (!#PDSCatchWeightEnabled)
    {
        fullfilment.columns(1);
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    element.setTitleText(true);
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    // <GEERU>
    countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    // </GEERU>

    salesAgreementHeaderDefault_ds.object(fieldNum(SalesAgreementHeaderDefault, EntryCertificateRequired_W)).visible(custParameters.EntryCertificateManagementEnabled_W);
    salesAgreementHeaderDefault_ds.object(fieldNum(SalesAgreementHeaderDefault, IssueOwnEntryCertificate_W)).visible(custParameters.EntryCertificateIssuingEnabled_W);

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
    }

    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        TransTaxInformation.visible(true);
    }
    // </GTE>

    if (element.args().parmEnumType() == enumNum(FormOpenMode))
    {
        Object caller = element.args().caller();
        if (caller && caller is FormRun)
        {
            skipLinkActive = element.args().parmEnum() == FormOpenMode::ForNew;
        }
    }

    if (ReportingCurrencyFixedExchangeRateFeature::isEnabled())
    {
        salesAgreementHeaderDefault_ReportingCurrencyFixedExchangeRate.visible(true);
        salesAgreementHeaderDefault_ReportingCurrencyFixedExchangeRate.enabled(Ledger::isReportingCurrencyDefined());
        salesAgreementHeaderDefault_ReportingCurrencyFixedExchangeRate.allowEdit(salesAgreementHeader.CustVendReportingCurrencyFixedExchangeRate::canEditReportingCurrencyFixedExchangeRate());
    }
}

