public void run()
{
    xArgs     curArgs = element.args();
    CustTable callerCustTable;

    // Filling the Sales Agreement args DTO.
    salesAgreementFormArgs.parmCallerDataSet(element.args().dataset());
    salesAgreementFormArgs.parmCallerRecord(element.args().record());

    if (!salesAgreementHeader.RecId
        && curArgs
        && curArgs.menuItemName()   == menuitemDisplayStr(SalesAgreementForNew))
    {
        closeOnCreationCancel = true;

        if (curArgs.dataset() == tableNum(CustTable))
        {
            // When form has been called to create a new SalesAgreement
            // and we have customer record passed via args
            // and SalesAgreementHeader record hasn't been saved yet...
            //
            // WE INITIALISE AccountNum field out of CustTable record passed with args.
            callerCustTable = curArgs.record();

            salesAgreementFormArgs.parmTransferAccountOnCreate(true);
            salesAgreementFormArgs.parmTransferCustVendAccountAccountNum(callerCustTable.AccountNum);
            salesAgreementFormArgs.parmTransferCustVendAccountDataAreaId(callerCustTable.DataAreaId);
        }
    }

    super();
}

/// <summary>
/// Sets proper title for the Sales Agreement form.
/// </summary>
/// <param name="_setInitialText">
/// A boolean value indicating whether the initial sales agreement text should be used as title.
/// </param>
public void setTitleText(boolean _setInitialText = false)
{
    if (_setInitialText)
    {
        headerTitle.text("@SYS309295");
    }
    else if (salesAgreementHeader.CustAccount)
    {
        headerTitle.text(strFmt('%1 : %2 - %3',
                                salesAgreementHeader.SalesNumberSequence,
                                salesAgreementHeader.CustAccount,
                                salesAgreementHeader.custName()));
    }
    else
    {
        headerTitle.text(strFmt('%1',"@SYS328764"));
    }
}

