/// <summary>
/// Prompts user about financial dimensions update and agreement lines update.
/// Performs necessary updates of every <c>AgreementLine</c> if confirmed by user.
/// </summary>
private void handleFinancialDimensionsLinesUpdate()
{
    DialogButton            financialDimensionsUpdateResponse;
    AgreementHeader         origAgreementHeader;

    if (salesAgreementHeader.RecId)
    {
        // Obtain valid Orig():
        // Due to ST/st buffer limitations you can't simply obtain original value by doing the following
        // origAgreementHeader = salesAgreementHeader.orig() - sub-type values won't be properly set!
        // Do following two steps instead - this way all fields will be assigned with proper values.
        origAgreementHeader = salesAgreementHeader;
        origAgreementHeader = origAgreementHeader.orig();

        if (!DimensionDefaultFacade::areEqual(salesAgreementHeader.DefaultDimension, origAgreementHeader.DefaultDimension) && ALine.RecId)
        {
            // Prompt user about financial dimensions lines update
            financialDimensionsUpdateResponse = Box::yesNo(
                "@SCM:FinancialDimensionsChanged_UpdateAgreementLinesWithHeaderValues_DialogLabel",
                DialogButton::Yes,
                "@SYS4004689");

            if (financialDimensionsUpdateResponse == DialogButton::Yes)
            {
                salesAgreementHeader.updateLinesDefaultDimension(salesAgreementHeader.DefaultDimension);
            }
        }
    }
}

public void linkActive()
{
    if (skipLinkActive)
    {
        return;
    }

    salesAgreementForm.setRangesForDynalinkActive(agreementHeaderDefault_ds);
    super();
}

private void initFromCaller()
{
    ProjTable   projTable;

    switch (element.args().dataset())
    {
        case tableNum(ProjTable):
            projTable = element.args().record();
            agreementHeaderDefault.initFromProjTable(projTable);
            if (agreementHeaderDefault_ds.validateProject())
            {
                agreementHeaderDefault_ds.modifiedProject();
            }
            else
            {
                agreementHeaderDefault.Project = '';
                agreementHeaderDefault.ProjectDataAreaId = '';
            }
            break;
    }
}

/// <summary>
/// Initializing the form data source Sales Agreement Header.
/// </summary>
/// <remarks>
/// Setting Sales Agreement Header record sorting to follow the Agreement ID and filters out
/// deleted records.
/// </remarks>
public void init()
{
    QueryBuildDataSource qbds;
    QueryBuildRange      qbr;
    super();

    qbds = this.query().dataSourceTable(tableNum(SalesAgreementHeader));

    qbr = qbds.addRange(fieldNum(SalesAgreementHeader, IsDeleted));
    qbr.value(queryValue(NoYes::No));
    qbr.status(RangeStatus::Hidden);

    qbr = qbds.addRange(fieldNum(SalesAgreementHeader, SellingLegalEntity));
    qbr.value(queryValue(CompanyInfo::current()));
    qbr.status(RangeStatus::Hidden);

    // Enable caching of the document handling...
    salesAgreementHeader_ds.cacheAddMethod(tableMethodStr(SalesAgreementHeader,showDocHandIcon));

    logisticsPostalAddress_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);

    logisticsPostalAddress_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(),
                                                                  DateTimeUtil::maxValue());
}

