/// <summary>
/// Initializing values on a newly created Sales Agreement Header record.
/// </summary>
public void initValue()
{
    super();

    salesAgreementHeader.DefaultAgreementLineType = salesAgreementForm.parmDefaultCommitmentType();

    aLine.clear();

    // <GEERU>
    if (countryRegion_RU)
    {
        salesAgreementHeaderExt_RU_ds.initValue();
        if (salesAgreementHeader.CustAccount)
        {
            salesAgreementHeaderDefault.initFromCustTable_RU(CustTable::find(salesAgreementHeader.CustAccount));
        }
    }
    else
    {
        // </GEERU>
        salesAgreementForm.formMethodDataSourceCreate(salesAgreementHeader_ds);
        // <GEERU>
    }
    // </GEERU>
    this.initFromCaller();
}

/// <summary>
/// Determines whether the current Sales Agreement Header is valid to be written.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
public boolean validateWrite()
{
    boolean ret;
    // <GEERU>
    if (countryRegion_RU)
    {
        if (salesAgreementHeader.AgreementClassification)
        {
            salesAgreementForm.formMethodDataSourceCreate(salesAgreementHeader_ds);
        }
        else
        {
            return checkFailed(strFmt("@SYS84753", fieldPName(SalesAgreementHeader, AgreementClassification)));
        }

        if (!salesAgreementHeaderExt_RU.AgreementDate)
        {
            return checkFailed(strFmt("@SYS84753", fieldPName(SalesAgreementHeaderExt_RU, AgreementDate)));
        }

        if (!salesAgreementHeaderExt_RU.checkAgreementUniqueness(salesAgreementHeader))
        {
            return false;
        }
    }
    // </GEERU>

    ret = super();

    if (   ret
        && agreementHeaderDefault_ds.object(fieldNum(AgreementHeaderDefault, Project)).mandatory()
        && !agreementHeaderDefault.Project )
    {
        ret = checkFailed(strFmt("@SYS84753", fieldPName(AgreementHeaderDefault, Project)));
    }

    if (ret && !salesAgreementForm.formMethodDataSourceValidateWrite(salesAgreementHeader_ds))
    {
        ret = false;
    }

    return ret;
}

/// <summary>
/// Committing the Number Sequence record and saving the current default commitment type before writing the
/// Sales Agreement Header record to the database.
/// Also takes care about possible currency code conversion.
/// </summary>
public void write()
{
    salesAgreementForm.formMethodDataSourceWrite(salesAgreementHeader_ds);

    // Save the default line commitment type
    salesAgreementForm.parmDefaultCommitmentType(salesAgreementHeader.DefaultAgreementLineType);
    salesAgreementForm.saveSysLastValue();

    super();

    // <GEERU>
    if (countryRegion_RU)
    {
        salesAgreementForm.refreshHeaderDefaultDimension();
        salesAgreementHeaderExt_RU_DS.reread();
        salesAgreementHeaderExt_RU_DS.refresh();
    }
    // </GEERU>
}

/// <summary>
/// Deleting the Sales Agreement Header from the database before aborting the Number Sequence record
/// and enabling or disabling the form buttons.
/// </summary>
public void delete()
{
    super();

    if (!salesAgreementHeader.RecId)
    {
        element.setTitleText(true);
    }

    salesAgreementForm.formMethodDataSourceDelete(salesAgreementHeader_ds);
}

/// <summary>
/// Creating a new Sales Agreement Header record getting the next Agreement ID from the number sequence
/// and enabling or disabling the form buttons.
/// </summary>
/// <param name="_append">
/// Boolean indicating whether the new record should be appended.
/// </param>
public void create(boolean _append = false)
{
    AgreementCreateResult createResult;

    createResult = salesAgreementForm.create();

    if (!createResult.parmCanceled())
    {
        if (skipLinkActive)
        {
            skipLinkActive = false;
            this.linkActive();
        }

        salesAgreementHeader.data(createResult.parmAgreementHeader() as SalesAgreementHeader);
        agreementHeaderDefault.data(createResult.parmAgreementHeaderDefault());
        salesAgreementHeaderDefault.data(createResult.parmPurchSalesAgreementHeaderDefault() as SalesAgreementHeaderDefault);
        salesAgreementHeaderExt_RU.data(createResult.parmAgreementHeaderExt_RU() as SalesAgreementHeaderExt_RU);
        tmsSalesAgreementHeader.data(createResult.parmTmsAgreementHeader() as TMSSalesAgreementHeader);

        // Rebuilding the shared code object with the new datasources.
        element.buildSharedDatasourceManager();

        // Refreshing the form with new datasource data.
        salesAgreementHeader_ds.rereadJoinHierarchy();
        salesAgreementHeader_ds.research(true);

        tmsSalesAgreementHeader.setCarrierFieldsFromDlvMode(agreementHeaderDefault.DeliveryMode);
        element.enableTMSFields();
        LineViewLines.setFocus();
    }
    else
    {
        // If the form was called with argument, this means that the form was opened to create a Sales Agreement.
        // If we don't create one, then we close the form.
        if (closeOnCreationCancel == true)
        {
            element.close();
        }
    }
    skipLinkActive = false;

    // This line forces a reset of the DEC when a new header is created
    // as on this form simply creating a new header doesn't refresh the
    // child data source consistently which causes the DEC to show
    // previous values.
    if (PurchSalesAgreementDimensionsClearOnCreateFlight::instance().isEnabled())
    {
        DimensionEntryControlLine.loadAttributeValueSet(0);
    }
}

