                                                                  DateTimeUtil::maxValue());
}

public void modifiedProject()
{
    ProjTable               projTable;
    CustTable               transferCustTable;
    ProjFundingSource       projFundingSource;
    LogisticsLocationEntity location;

    if (agreementHeaderDefault.Project)
    {
        projTable = ProjTable::find(agreementHeaderDefault.Project);

        transferCustTable = CustTable::find(projTable.CustAccount);
        select firstonly CustAccount, LanguageId from projFundingSource where projFundingSource.ContractId == projTable.ProjInvoiceProjId;

        salesAgreementHeader.CustAccount = projTable.CustAccount;
        salesAgreementHeader.initFromCustTable();
        salesAgreementHeader.Language = projFundingSource.LanguageId;
        salesAgreementHeader.Currency = projTable.currencyId();
        salesAgreementHeader.initFromProjTable(projTable);

        salesAgreementHeaderDefault.initFromCustTable(transferCustTable);
        salesAgreementHeaderDefault.CustomerInvoiceAccount = projFundingSource.CustAccount;
        salesAgreementHeaderDefault.CustomerInvoiceAccountDataAreaId = appl.company().dataArea(tableNum(CustTable));

        agreementHeaderDefault.initFromCustTable(transferCustTable);
        location = LogisticsLocationEntity::constructFromLocationRecId(projTable.DeliveryLocation);
        agreementHeaderDefault.DeliveryName =  location.getPostalAddress().displayLocationDescription();
        agreementHeaderDefault.DeliveryPostalAddress = location.getPostalAddress().RecId;
    }
}

public boolean validateProject()
{
    boolean     ret = true;
    ProjTable   projTable;

    if (agreementHeaderDefault.Project)
    {
        projTable = ProjTable::find(agreementHeaderDefault.Project);
        if (!projTable.type().ctrlSalesOrderTable())
        {
            ret = checkFailed(strFmt("@SYS53011", projTable.Type));
        }

        if (ret)
        {
            if (!projTable.isSalesOrderAllowedForMultipleFundingSources())
            {
                ret = checkFailed("@SYS138648");
            }
        }
    }

    return ret;
}

