public boolean validateProject()
{
    boolean ret = true;

    if (agreementHeaderDefault.Project)
    {
        if (!aLine.ProjectProjId)
        {
            ret = checkFailed("@SYS50152");
        }
        else
        {
            ret = ProjTable::find(aLine.ProjectProjId).status().validateWriteSalesLine();
        }
    }

    return ret;
}

