/// <summary>
/// Enabling or disabling Agreement Line and Inventory Dimensions fields based on the active record and
/// setting the form caption text.
/// </summary>
/// <returns>
/// Result of the super call.
/// </returns>
public int active()
{
    if (kittingFormHandler)
    {
        kittingFormHandler.DS_PreActive(this);
    }

    int ret = super();

    // In case of a research and there are no more records the datasource gets activated
    // but there is no valid record and this will throw an excpetion in salesAgreementForm.enableLineFields().
    if (ALine.Agreement)
    {
        var editabilityDataProvider = AgreementLineFieldEditabilityDataProvider::newFromAgreementLine(ALine);

        salesAgreementForm.enableLineFields(aLine_ds, aLine_agreementLineQuantityCommitment_ds, editabilityDataProvider);

        salesAgreementForm.enableLineDefaultFields(agreementLineDefault_ds, editabilityDataProvider);

        this.enableNetAmount();

        inventDim_ds.allowEdit(!editabilityDataProvider.parmIsLineReleased());

        element.updateDesign(InventDimFormDesignUpdate::Active);
        element.setTitleText();

        // If Fulfillment tab visible - query reminders.
        if (tabPageFullfilment.isActivePage())
        {
            element.updateFollowUpFields();
        }

        if (kittingFormHandler)
        {
            kittingFormHandler.activeAgreementDs(this, InventDim_ds, ALine_AgreementLineQuantityCommitment_ds);
        }
    }
    return ret;
}

