/// <summary>
/// Called when the Agreement Line Item ID is modified.
/// </summary>
/// <remarks>
/// Initializing Agreement Line values from Inventory Table and enabling or disabling fields and setting
/// the form caption text.
/// </remarks>
public void modified()
{
    boolean setDefaultProjectCategory = false;

    super();

    if (kittingFormHandler && kittingFormHandler.isDynKit(ALine))
    {
        aLine_ds.research();
        return;
    }

    if (productFromLookup) // if there was product selected from the product lookup
    {
        if (inventDim.copyProductDimensions(InventDim::find(productFromLookup.InventDimId),
                                            aLine.ItemId))
        {
            aLine.InventDimId = InventDim::findOrCreate(inventDim).inventDimId;
            aLine.name();
        }
        aLine_DS.cacheCalculateMethod(tableMethodStr(AgreementLine, name));
        productFromLookup = null;
    }

    if (aLine.ProjectProjId)
    {
        // If project category is set for the AgreementLine buffer
        // we need to default ProjectCategory for AgreementDefault buffer!
        // EVEN FOR NOT YET SAVED RECORD.
        setDefaultProjectCategory = true;
    }

    agreementLineDefault.initFromInventTable(aLine.inventTable(), setDefaultProjectCategory);

    if (aLine.isQuantityBased())
    {
        aLine_agreementLineQuantityCommitment.initFromInventTable();
        // <GEERU>
        if (countryRegion_RU)
        {
            agreementLineExt_RU.initFromInventTable(aLine_agreementLineQuantityCommitment.inventTable());
            agreementLineExt_RU_ds.refresh();
        }
        // </GEERU>
    }

    element.updateDesign(InventDimFormDesignUpdate::FieldChange);

    salesAgreementForm.enableLineQuantityFieldsDependentOnItem(aLine_agreementLineQuantityCommitment_ds);

    element.setTitleText();

    aLine_ds.refresh();
    inventDim.data(InventDim::find(aLine.InventDimId));

    if (kittingFormHandler)
    {
        if (kittingFormHandler.isKit())
        {
            kittingFormHandler.setLineAccess(ALine_DS, InventDim_DS);
        }
        aLine_ds.research();
    }
}

