    {
        return;
    }

    super();
}

/// <summary>
/// Provides a lookup for the line itemId field.
/// </summary>
/// <param name="_formControl">
/// This is the default parameter for referece of lookup control
/// </param>
/// <param name="_filterStr">
/// Selected string from the lookup
/// </param>
public void lookup(FormControl _formControl, str _filterStr)
{
    if (EcoResProductParameters::find().pcLookupMethod == PCLookupMethod::Configurable)
    {
        Args args = new Args(formStr(PCProductLookup));
        args.caller(element);

        FormRun formRun = classfactory.formRunClass(args);
        formRun.init();

        this.performFormLookup(formRun, _formControl);
    }
    else if (EcoResProductSearchLookup::isProductSearchLookupEnabled())
    {
        EcoResProductSearchLookup::lookupProduct(_formControl as FormStringControl);
    }
    else
    {
        InventTable::lookupItem(_formControl);
    }
}

