public boolean validateCategory()
{
    ProjValCheckTrans   projValCheckTrans = new ProjValCheckTrans();
    ProjCategory        projCategory;
    boolean             ret = true;

    if (agreementHeaderDefault.Project)
    {
        if (!agreementLineDefault.ProjectCategory)
        {
            ret = checkFailed("@SYS98169");
        }
        else
        {
            projCategory = ProjCategory::find(agreementLineDefault.ProjectCategory);
            if (projCategory.CategoryType != ProjCategoryType::Item)
            {
                ret  = checkFailed(strFmt("@SYS56468", ProjCategoryType::Item));
            }
            else
            {
                ret = projCategory.validateIfInactiveAllowed();
                ret  = ret && projValCheckTrans.validateMandatory(agreementLineDefault, aLine);
            }
        }
    }

    return ret;
}

/// <summary>
/// Determines whether the current Agreement Line Default is valid to be written.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
public boolean validateWrite()
{
    boolean ret;

    if (!agreementLineDefault.AgreementLine)
    {
        agreementLineDefault.AgreementLine = -1;
    }

    ret = super();

    ret = ret && agreementLineDefault_ds.validateCategory();

    if (agreementLineDefault.AgreementLine == -1)
    {
        agreementLineDefault.AgreementLine = 0;
    }

    return ret;
}

