{
    boolean doRefreshDim = false;

    if (countryRegion_RU)
    {
        doRefreshDim = salesAgreementHeaderExt_RU.RecId == 0;

        super();

        if (doRefreshDim)
        {
            salesAgreementHeader_ds.reread();
            salesAgreementHeader_ds.refresh();
        }
    }
}

public boolean validateWrite()
{
    boolean ret = true;

    if (countryRegion_RU)
    {
        if (!salesAgreementHeaderExt_RU.AgreementHeader)
        {
            if (!salesAgreementHeader.RecId)
            {
                salesAgreementHeader_ds.write();
            }
            salesAgreementHeaderExt_RU.initFromAgreementHeader(salesAgreementHeader);
        }

        ret = super();
    }

    return ret;
}

