void clicked()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();

    if (salesAgreementHeader_ds.cursor() == null)
        return;

    super();

    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Sales, PrintMgmtNodeType::SalesAgreement);
    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(salesAgreementHeader_ds.cursor());
    setupContext.parmCountryRegionId(salesAgreementHeader.custTable().postalAddress().CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}

