/// <summary>
/// Analyzes whether form can be closed.
/// </summary>
/// <returns>
/// True if form can be closed, false - if some conflicts are detected in forms data and user action wasn't Cancel.
/// </returns>
boolean  canClose()
{
    salesAgreementGenerateReleaseOrder.parmReleaseOrderLine(salesCreateReleaseOrderLineTmp);
    salesAgreementGenerateReleaseOrder.parmReleaseOrderTable(salesCreateReleaseOrderTableTmp);

    if (!element.closedOk())
    {
        return super();
    }

    if (!salesAgreementGenerateReleaseOrder.check())
    {
        salesCreateReleaseOrderLineTmp_ds.reread();
        salesCreateReleaseOrderLineTmp_ds.refresh();
        return false;
    }

    return super();
}

/// <summary>
/// Calculates whether the Inventory Dimensions field with provided ID can be editable during the release from agreement line
/// with specified RecId
/// </summary>
/// <param name="_inventDimFieldId">
/// InventDim field Id to be tested.
/// </param>
/// <param name="_agreementLineRecId">
/// RecId of the <c>AgreementLineQuantityCommitment</c> used for release.
/// </param>
/// <returns>
/// True if particular InventDim field is cached as editable, false - if not.
/// </returns>
/// <remarks>
/// Used cached information about field editability produced in init method.
/// </remarks>
boolean canEditInventDimField(FieldId _inventDimFieldId, int64 _agreementLineRecId)
{
    boolean ret = false;
    InventDimParm   inventDimEditableFields;

    if (editableInventDimFieldsPerLine && editableInventDimFieldsPerLine.exists(_agreementLineRecId))
    {
        inventDimEditableFields = editableInventDimFieldsPerLine.lookup(_agreementLineRecId);

        return inventDimEditableFields.isFieldIdEnabled(_inventDimFieldId);
    }
    return ret;
}

/// <summary>
/// Copies delivery address information to the line.
/// </summary>
/// <param name="_copyAddress">
/// Dialog button.
/// </param>
void copyAddressToLine(DialogButton _copyAddress)
{
    SalesCreateReleaseOrderLineTmp releaseOrderLineTmp;

    ttsbegin;

    releaseOrderLineTmp.setTmpData(salesCreateReleaseOrderLineTmp);

    while select releaseOrderLineTmp
        where releaseOrderLineTmp.AgreementLineQuantityCommitment &&
        (_copyAddress == DialogButton::YesToAll ||
         releaseOrderLineTmp.DeliveryPostalAddress == deliveryPostalAddressOrig)
    {
        releaseOrderLineTmp.DeliveryName          = salesCreateReleaseOrderTableTmp.DeliveryName;
        releaseOrderLineTmp.DeliveryPostalAddress = salesCreateReleaseOrderTableTmp.DeliveryPostalAddress;
        releaseOrderLineTmp.AddressRefRecId       = salesCreateReleaseOrderTableTmp.AddressRefRecId;
        releaseOrderLineTmp.AddressRefTableId     = salesCreateReleaseOrderTableTmp.AddressRefTableId;

        releaseOrderLineTmp.doUpdate();
    }

    ttscommit;

    salesCreateReleaseOrderLineTmp_ds.research();
}

/// <summary>
/// Initializes internal form state.
/// </summary>
void init()
{
    int inventDimColumnCounter;

    super();

    salesAgreementGenerateReleaseOrder = element.args().caller();
    salesAgreementHeader = salesAgreementGenerateReleaseOrder.parmSalesAgreementHeader();

    salesAgreementGenerateReleaseOrder.createReleaseOrderTable(salesCreateReleaseOrderTableTmp);
    salesAgreementGenerateReleaseOrder.createReleaseOrderLine(salesCreateReleaseOrderLineTmp);

    // Init a cached map of editable Inventory Dimensions fields per AgreementLineQuantityCommitment...
    editableInventDimFieldsPerLine = new Map (Types::Int64,Types::Record);

    // ...populate it with information
    editableInventDimFieldsPerLine = salesAgreementGenerateReleaseOrder.getEditableInventDimFieldsPerLines();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    inventDimColumnCounter = conLen(inventDimFormSetup.dimFieldsActive(inventDimFormSetup.parmDimParmVisibleGrid()));

    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    pdsCWFormCtrl = PdsCWFormCtrl_SalesAgreeReleaseOrder::newFromForm(this,SalesCreateReleaseOrderLineTmp);
}

