/// <summary>
/// When delivery address is changed prompts if copy to line is needed and invokes copyAddressToLine method to perform copying when requested.
/// </summary>
void updateDeliveryAddress()
{
    DialogButton copyAddressToLines;

    if (salesCreateReleaseOrderTableTmp.DeliveryPostalAddress && salesCreateReleaseOrderTableTmp.DeliveryPostalAddress !=  deliveryPostalAddressOrig)
    {
        element.copyAddressToLine(copyAddressToLines);
    }
}

/// <summary>
/// Updates form design elements.
/// </summary>
/// <param name="mode">
/// Mode describing purpose for the call.
/// </param>
/// <exception cref="Exception::Error">
/// Throws when called with non-supported mode.
/// </exception>
public void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_AgreementLine::newFromForm(element, fieldNum(InventDimSetupGrid, ShowGridSalesLine));
            }
            break;
        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(salesCreateReleaseOrderLineTmp.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(salesCreateReleaseOrderLineTmp.ItemId));
            inventDim.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

/// <summary>
/// Method to update line information according to changes in header.
/// </summary>
/// <param name="_fieldId">
/// Field that has been changed on header.
/// </param>
void updateLinesFromHeader(FieldId _fieldId)
{
    if (salesCreateReleaseOrderTableTmp.(_fieldId)              != salesCreateReleaseOrderTableTmp.orig().(_fieldId)              ||
        salesCreateReleaseOrderTableTmp.ReceiptDateRequested    != salesCreateReleaseOrderTableTmp.orig().ReceiptDateRequested    ||
        salesCreateReleaseOrderTableTmp.ShippingDateRequested   != salesCreateReleaseOrderTableTmp.orig().ShippingDateRequested   ||
        salesCreateReleaseOrderTableTmp.DlvMode                 != salesCreateReleaseOrderTableTmp.orig().DlvMode                 ||
        salesCreateReleaseOrderTableTmp.InventSiteId            != salesCreateReleaseOrderTableTmp.orig().InventSiteId            ||
        salesCreateReleaseOrderTableTmp.InventLocationId        != salesCreateReleaseOrderTableTmp.orig().InventLocationId        ||
        salesCreateReleaseOrderTableTmp.DeliveryDateControlType != salesCreateReleaseOrderTableTmp.orig().DeliveryDateControlType ||
        salesCreateReleaseOrderTableTmp.DeliveryName            != salesCreateReleaseOrderTableTmp.orig().DeliveryName            ||
        salesCreateReleaseOrderTableTmp.DeliveryPostalAddress   != salesCreateReleaseOrderTableTmp.orig().DeliveryPostalAddress)
    {
        salesAgreementGenerateReleaseOrder.updateSalesCreateReleaseOrderLineTmp(salesCreateReleaseOrderTableTmp,salesCreateReleaseOrderLineTmp,_fieldId);
        salesCreateReleaseOrderTableTmp.write();
        salesCreateReleaseOrderLineTmp_ds.executeQuery();
    }
}

