public void modified()
{
    salesCreateReleaseOrderLineTmp.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    super();

    salesCreateReleaseOrderLineTmp_DS.cacheCalculateMethod(tableMethodStr(SalesCreateReleaseOrderLineTmp, recommendedQty));
    salesCreateReleaseOrderLineTmp.SalesQty = decRound(salesCreateReleaseOrderLineTmp.SalesQty, UnitOfMeasure::findBySymbol(salesCreateReleaseOrderLineTmp.Units).DecimalPrecision);
}

public void write()
{
    SalesCreateReleaseOrderTableTmp orig = salesCreateReleaseOrderTableTmp.orig();

    if (orig.DeliveryPostalAddress != salesCreateReleaseOrderTableTmp.DeliveryPostalAddress)
    {
        element.updateLinesFromHeader(fieldNum(SalesCreateReleaseOrderTableTmp,DeliveryPostalAddress));
    }

    super();
}

