/// <summary>
/// Sets AllowEdit property for the data source field
/// </summary>
/// <param name="_fieldId">
/// Id of the field to set.
/// </param>
protected void setUpEditableInventDimField(FieldId _fieldId)
{
    FormDataObject inventDimFieldObject;

    inventDimFieldObject = this.object(_fieldId);

    if (_fieldId &&  inventDimFieldObject)
    {
        if (!element.canEditInventDimField(_fieldId, salesCreateReleaseOrderLineTmp.AgreementLineQuantityCommitment))
        {
            inventDimFieldObject.allowEdit(false);
        }
        else
        {
            inventDimFieldObject.allowEdit(true);
        }
    }
}

/// <summary>
/// Sets AllowEdit property for inventory dimensions fields depending on the inventory dimensions set-up for the active
/// line
/// </summary>
/// <remarks>
/// Watches only the following fields: product dimensions, InventSiteId, InventLocationId
/// </remarks>
protected void setUpEditableDimensions()
{
    InventDimensionEnumerator productDims = InventProductDimension::getEnumeratorForEnabledDimensions();
    while (productDims.moveNext())
    {
        this.setUpEditableInventDimField(productDims.currentInventDimFieldId());
    }

    this.setUpEditableInventDimField(fieldNum(InventDim, InventSiteId));
    this.setUpEditableInventDimField(fieldNum(InventDim, InventLocationId));
}

public int active()
{
    int ret;

    ret = super();

    this.setUpEditableDimensions();

    return ret;
}

