[Form]
public class SalesAgreementHistory extends FormRun implements OfficeIMenuCustomizer
{
    SalesAgreementHeader            salesHeader;

    InventDimCtrl_Frm_AgreementLine inventDimFormSetup;

    CustParameters                  custParameters;

}

public void init()
{
    if (element.args().record())
    {
        custParameters = CustParameters::find();

        if (element.args().dataset() == tableNum(SalesAgreementHeader))
        {
            salesHeader = element.args().record();
        }
        else if (element.args().dataset() == tableNum(SalesAgreementHeaderHistory))
        {
            SalesAgreementHeaderHistory history = element.args().record();

            salesHeader = SalesAgreementHeader::find(history.AgreementHeader);
        }
        else
        {
            throw error(Error::wrongUseOfFunction(formStr(SalesAgreementHistory)));
        }

        if (salesHeader)
        {
            super();
            element.updateDesign(InventDimFormDesignUpdate::Init);
            logisticsPostalAddress_Sales_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
        }

        salesAgreementHeaderDefaultHistory_ds.object(fieldNum(SalesAgreementHeaderDefaultHistory, EntryCertificateRequired_W)).visible(custParameters.EntryCertificateManagementEnabled_W);
        salesAgreementHeaderDefaultHistory_ds.object(fieldNum(SalesAgreementHeaderDefaultHistory, IssueOwnEntryCertificate_W)).visible(custParameters.EntryCertificateIssuingEnabled_W);

        if (ReportingCurrencyFixedExchangeRateFeature::isEnabled())
        {
            SalesAgreementHeaderDefaultHistory_ReportingCurrencyFixedExchangeRate.visible(true);
        }
    }
    else
    {
        throw error(Error::missingFormActiveBuffer(formStr(SalesAgreementHistory)));
    }
}

/// <summary>
/// Customizes the options used to populate the Office Menu with the sales and marketing entities.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    str salesAndMarketingModule = enum2Symbol(EnumNum(DMFModule), DMFModule::SalesAndMarketing);

    while (dataEntityOptionsIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityOptionsIterator.value();

        DictDataEntity dataEntity = new DictDataEntity(tablename2Id(dataEntityOptions.dataEntityName()));

        if (dataEntity.modules() != salesAndMarketingModule)
        {
            dataEntityOptionsIterator.delete();
        }
        else
        {
            dataEntityOptionsIterator.next();
        }
    }
}

/// <summary>
/// Gets the Inventory Dimensions Form Setup object.
/// </summary>
/// <returns>
/// Inventory Dimensions Form Setup object.
/// </returns>
public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

/// <summary>
/// Updates form design elements.
/// </summary>
/// <param name="_mode">
/// Mode describing purpose for the call.
/// </param>
/// <exception cref="Exception::Error">
/// Throws when called with non-supported mode.
/// </exception>
public void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                if (salesHeader)
                {
                    inventDimFormSetup = InventDimCtrl_Frm_AgreementLine::newFromForm(element, fieldNum(InventDimSetupGrid, ShowGridSalesLine));
                }
            }

        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(a.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

void setRangeValues()
{
    QueryBuildDataSource    ds;
    QueryBuildRange         range;

    ds = a_ds.query().dataSourceTable(tableNum(AgreementLineHistory));
    range = ds.rangeField(fieldNum(AgreementLineHistory, ValidFrom));
    range.value(SysQuery::range(null, salesAgreementHeaderHistory.CreatedDateTime));

    range = ds.rangeField(fieldNum(AgreementLineHistory, ValidTo));
    range.value(SysQuery::range(salesAgreementHeaderHistory.CreatedDateTime, null));

    ds = a_ds.query().dataSourceTable(tableNum(AgreementLine));
    SysQuery::findOrCreateRange(ds, fieldNum(AgreementLine, Agreement))
        .value(queryValue(salesAgreementHeaderHistory.AgreementHeader));

    a_ds.executeQuery();
}

public int active()
{
    int ret;

    ret = super();

    this.setRangeValues();

    return ret;
}

public void init()
{
    QueryBuildDataSource ds;
    QueryBuildDataSource subDs;

    super();

    ds = this.query().dataSourceTable(this.table());
    ds.clearRanges();
    ds.clearDynalinks();
    ds.clearLinks();

    ds.addRange(fieldNum(AgreementLineHistory, ValidFrom));
    ds.addRange(fieldNum(AgreementLineHistory, ValidTo));

    subDs = ds.addDataSource(tableNum(AgreementLine));
    subDs.relations(true);
    subDs.joinMode(JoinMode::InnerJoin);
    SysQuery::findOrCreateRange(subDs, fieldNum(AgreementLine, Agreement))
        .value(queryValue(salesAgreementHeaderHistory.AgreementHeader));
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

