public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    Map filtersToApply = new Map(Types::String, Types::Class);

    switch (_menuItem.dataEntityName())
    {
        case dataentityviewstr(SalesAgreementHeaderEntity):
            filtersToApply.insert(
                _menuItem.dataEntityName(),
                this.createFilterWithCompany(_menuItem.dataEntityName()));
            break;
    }

    return filtersToApply;
}

