[Form]
public class SalesBookPrepayments_RU extends FormRun
{
    #define.LessThanZero("<0")
    CustTrans           custTransLink;
    CustTransDetails    custTransDetails;
    Map                 markTransMap;
    Map                 custUseOriginalDocumentAsFactureMap;

}

public UseOriginalDocumentAsFacture_RU getUseOriginalDocumentAsFacture(CustAccount _custAccount)
{
    UseOriginalDocumentAsFacture_RU useOriginalDocumentAsFacture;
    if (custUseOriginalDocumentAsFactureMap.exists(_custAccount))
    {
        useOriginalDocumentAsFacture =  custUseOriginalDocumentAsFactureMap.lookup(_custAccount);
    }
    else
    {
        useOriginalDocumentAsFacture = CustTable::find(_custAccount).UseOriginalDocumentAsFacture_RU;
        custUseOriginalDocumentAsFactureMap.insert(_custAccount, useOriginalDocumentAsFacture);
    }
    return useOriginalDocumentAsFacture;
}

void handlePrepayment()
{
    SalesBookPrepayment_RU      salesBookPrepayment = SalesBookPrepayment_RU::construct(markTransMap.pack());

    if (salesBookPrepayment.prompt())
    {
        salesBookPrepayment.runOperation();
    }
    element.initTransMap();
    custTrans_DS.research();
}

public void init()
{
    Args args = this.args();

    element.initTransMap();
    custUseOriginalDocumentAsFactureMap = new Map(Types::String, Types::Enum);

    if (args && args.dataset() == tableNum(CustTrans))
    {
        custTransLink = args.record();
    }

    super();

    custTransDetails.initBalanceList_RU(balanceList);
}

void markUnmarkLines(NoYes _mark)
{
    QueryRun    qr = new QueryRun(custTrans_QR.query().pack());
    CustTrans   custTransMark, currentTrans;

    currentTrans.data(custTrans);

    if (_mark == NoYes::Yes)
    {
        while (qr.next())
        {
            custTransMark = qr.get(tableNum(CustTrans));
            markTransMap.insert(custTransMark.RecId, [dateNull(), this.getUseOriginalDocumentAsFacture(custTransMark.AccountNum)]);
        }
    }
    else
    {
        element.initTransMap();
    }

    custTrans_DS.research();
    element.args().lookupRecord(currentTrans);
    custTrans_ds.research(false);
    custTrans_DS.reread();
}

edit NoYes markUseOriginalDocumentAsFacture(boolean    _set,
                     CustTrans  _custTrans,
                     NoYes      _markTrans)
{
    NoYes       res;
    TransDate curValueTransDate;
    UseOriginalDocumentAsFacture_RU curValueUseOriginalDocumentAsFacture;

    if(_set)
    {
        if(markTransMap.exists(_custTrans.RecId))
        {
            [curValueTransDate, curValueUseOriginalDocumentAsFacture] = markTransMap.lookup(_custTrans.RecId);
            markTransMap.insert(_custTrans.RecId, [curValueTransDate, _markTrans]);
        }
        res = _markTrans;
    }
    else
    {
        if(markTransMap.exists(_custTrans.RecId))
        {
            [curValueTransDate, curValueUseOriginalDocumentAsFacture] = markTransMap.lookup(_custTrans.RecId);
            res = curValueUseOriginalDocumentAsFacture;
        }
        else
        {
            res = element.getUseOriginalDocumentAsFacture(_custTrans.AccountNum);
        }

    }

    return res;
}

edit NoYes markTransSameDate(boolean    _set,
                     CustTrans  _custTrans,
                     NoYes      _markTrans)
{
    NoYes       res;
    TransDate curValueTransDate;
    UseOriginalDocumentAsFacture_RU curValueUseOriginalDocumentAsFacture;

    if(_set)
    {
        if (_markTrans == NoYes::Yes)
        {
            if(markTransMap.exists(_custTrans.RecId))
            {
                [curValueTransDate, curValueUseOriginalDocumentAsFacture] = markTransMap.lookup(_custTrans.RecId);
                markTransMap.insert(_custTrans.RecId, [_custTrans.TransDate, curValueUseOriginalDocumentAsFacture]);
            }
            res = true;
        }
        else
        {
            if(markTransMap.exists(_custTrans.RecId))
            {
                [curValueTransDate, curValueUseOriginalDocumentAsFacture] = markTransMap.lookup(_custTrans.RecId);
                markTransMap.insert(_custTrans.RecId, [dateNull(), curValueUseOriginalDocumentAsFacture]);
            }
            res = false;
        }
    }
    else
    {
        if(markTransMap.exists(_custTrans.RecId))
        {
            [curValueTransDate, curValueUseOriginalDocumentAsFacture] = markTransMap.lookup(_custTrans.RecId);
            res = curValueTransDate == _custTrans.TransDate;
        }
        else
        {
            res = false;
        }

    }

    return res;
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        custTransDetails.setCustVendTrans(custTrans);
        custTransDetails.createBalanceList_RU(balanceList);
    }

    return ret;
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceName(tableStr(CustTrans));

    if (custTransLink)
    {
        qbds.addDynalink(fieldNum(CustTrans, RecId), custTransLink, fieldNum(CustTrans, RecId));
    }
    else
    {
        qbds.addRange(fieldNum(CustTrans,Prepayment)).value(queryValue(NoYes::No));
        qbds.addRange(fieldNum(CustTrans,Invoice)).value(SysQuery::valueEmptyString());
        qbds.addRange(fieldNum(CustTrans,Closed)).value(queryValue(dateNull()));

        qbds.addSortField(fieldNum(CustTrans,AccountNum), SortOrder::Ascending);
    }

    custTransDetails = new CustTransDetails(custTrans);
}

edit NoYes markTrans(boolean    _set,
             CustTrans  _custTrans,
             NoYes      _markTrans)
{
    NoYes   res;

    if(_set)
    {
        if (_markTrans == NoYes::Yes)
        {
            markTransMap.insert(_custTrans.RecId, [dateNull(), element.getUseOriginalDocumentAsFacture(_custTrans.AccountNum)]);
            res = true;
        }
        else
        {
            if(markTransMap.exists(_custTrans.RecId))
            {
                markTransMap.remove(_custTrans.RecId);
            }
            res = false;
        }
    }
    else
    {
        res = markTransMap.exists(_custTrans.RecId);
    }

    return res;
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceName(tableStr(CustTransOpen));

    qbds.addRange(fieldNum(CustTransOpen, AmountCur)).value(#LessThanZero);
}

void clicked()
{
    FormRun         formRun;
    CustTrans       custTransLocal;
    Args            args = new Args(this.menufunction().object());

    custTransLocal.data(custTrans);

    args.record(custTable);

    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.detach();
    formRun.wait();

    custTrans_ds.research();
    custTrans_ds.reread();
    element.args().lookupRecord(custTransLocal);
    custTrans_ds.research(false);
    custTrans_ds.cacheCalculateMethod(tableMethodStr(CustTrans, canSettle_RU));
}
