[Form]
    public class SalesBookTrans_RU extends FormRun
    {
        SalesBookTable_RU               salesBookTable;

        QueryBuildRange                 vendorAccountNum;

        Map                             moduleCache;
        Map                             vatrateCache;

        FactureOperationCodesUpdate_RU  operCodesUpdate;
        Set                             markedLinesSet;

}

FactureModule_RU getModuleCached()
{
    FactureModule_RU ret;

    if (moduleCache.exists(salesBookTrans.RecId))
    {
        ret = moduleCache.lookup(salesBookTrans.RecId);
    }
    else
    {
        ret = salesBookTrans.TransType != SalesPurchBookTransType_RU::VATRestoring ?
              SalesBookVATProcessLogTrans_RU::transType2FactureModule(salesBookTrans.TransType,
                                                                      salesBookTrans.FactureId,
                                                                      salesBookTrans.FactureDate,
                                                                      0)           :
              FactureModule_RU::Vend;

        moduleCache.insert(salesBookTrans.RecId, ret);
    }

    return ret;
}

void init()
{
    if (this.args().dataset() != tableNum(SalesBookTable_RU) || ! this.args().record())
    {
        throw error(strFmt("@SYS19096", element.name(), tableStr(SalesBookTable_RU)));
    }

    salesBookTable = this.args().record();

    moduleCache = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Enum);

    if (this.args().caller() && this.args().caller() is FactureOperationCodesUpdate_RU)
    {
        operCodesUpdate = this.args().caller();
        markedLinesSet = Set::create(operCodesUpdate.parmMarkedLinesSet());
    }

    super();

    element.initControls();
}

void setContragentVisible()
{
    FactureModule_RU module = element.getModuleCached();

    customer.visible(false);
    custPaymentTrans.visible(false);
    custFacture.visible(false);

    vendor.visible(false);
    vendPaymentTrans.visible(false);
    vendFacture.visible(false);
    vendFacture_TaxCorrection.visible(false);

    if (salesBookTrans.RecId)
    {
        switch (module)
        {
            case FactureModule_RU::Vend                    :
            case FactureModule_RU::TaxCorrectionSales_Vend :
                vendFacture.visible(module == FactureModule_RU::Vend);
                vendFacture_TaxCorrection.visible(module == FactureModule_RU::TaxCorrectionSales_Vend);
                vendorAccountNum.value(salesBookTrans.AccountNum);
                vendTable_DS.executeQuery();
                vendor.visible(true);
                vendPaymentTrans.visible(true);
                break;

            default :
                customer.visible(true);
                custPaymentTrans.visible(true);
                custFacture.visible(true);
        }
    }
    else
    {
        vendor.visible(false);
        customer.visible(true);
        custPaymentTrans.visible(true);
        custFacture.visible(true);
    }
}

public void closeOk()
{
    if (operCodesUpdate)
    {
        operCodesUpdate.parmMarkedLinesSet(markedLinesSet.pack());
    }

    super();
}

public void initControls()
{
    DictTable dictTable = new DictTable(tableNum(SalesBookTrans_RU));
    int i;
    boolean isOperCodeUpdate = operCodesUpdate ? true : false;

    for (i = 1; i <= dictTable.fieldCnt(); i++)
    {
        if (!dictTable.fieldObject(dictTable.fieldCnt2Id(i)).isSystem())
        {
            SalesBookTrans_DS.object(dictTable.fieldCnt2Id(i)).allowEdit(false);
        }
    }

    SalesBookTrans_DS.object(fieldNum(SalesBookTrans_RU, OperationTypeCodes)).allowEdit(true);

    MarkLine.visible(isOperCodeUpdate);
    FactureOperationTypeCodes.visible(isOperCodeUpdate);

    TotalAmountsBtn.visible(!isOperCodeUpdate);
    UpdateBookBtn.visible(!isOperCodeUpdate);
    MenuButtonPrint.visible(!isOperCodeUpdate);

    CommandButtonOK.visible(isOperCodeUpdate);
    CommandButtonCancel.visible(isOperCodeUpdate);
    MarkAllButton.visible(isOperCodeUpdate);
}

public void updateControls()
{
    if (operCodesUpdate)
    {
        SalesBookTrans_DS.object(fieldnum(SalesBookTrans_RU, OperationTypeCodes)).allowEdit(!markedLinesSet.in(salesBookTrans.RecId));
    }
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = salesBookTrans_Q.dataSourceTable(tableNum(SalesBookTrans_RU));
    qbds.clearDynalinks();

    if (operCodesUpdate)
    {
        this.query(new Query(operCodesUpdate.queryRun().query().pack())).userUpdate(false);
    }
    else
    {
        qbds.addDynalink(fieldNum(SalesBookTrans_RU, SalesBookTable_RU), salesBookTable, fieldNum(SalesBookTable_RU, RecId));
    }
}

public void linkActive()
{
    super();

    element.setContragentVisible();
}

public int active()
{
    int                 ret;

    ret = super();

    element.setContragentVisible();

    custPaymentTrans.enabled(salesBookTrans.PaymentRecIdRef);
    correctedFactureGrid.visible(salesBookTrans.JointCorrection);
    correctedFactureExternalId.visible(! salesBookTrans.JointCorrection);
    correctedFactureDate.visible(! salesBookTrans.JointCorrection);

    element.setLabels();
    element.updateControls();

    return ret;
}

// BP deviation documented
public display FactureOperationTypeCodes_RU factureOperationTypeCodes(SalesBookTrans_RU _salesBookTrans)
{
    FactureOperationTypeCodes_RU operationTypeCodes;

    if (operCodesUpdate)
    {
        operationTypeCodes = operCodesUpdate.getNewFactureOperationTypeCode(_salesBookTrans.operationTypeCodes);
    }

    return operationTypeCodes;
}

// BP deviation documented
public edit boolean editMarkLine(
    boolean             _set,
    SalesBookTrans_RU   _salesBookTrans,
    boolean             _markLine)
{
    if (_set)
    {
        if (_markLine)
        {
            markedLinesSet.add(_salesBookTrans.RecId);
        }
        else
        {
            markedLinesSet.remove(_salesBookTrans.RecId);
        }
    }

    return markedLinesSet.in(_salesBookTrans.RecId);
}

public void jumpRef()
{
    Args    args = new Args();

    switch (element.getModuleCached())
    {
        case FactureModule_RU::Cust :
            super();
            break;

        case FactureModule_RU::Vend :
            args.record(VendTable::find(salesBookTrans.AccountNum));
            new MenuFunction(menuitemDisplayStr(VendTable), MenuItemType::Display).run(args);
            break;

        default :
    }
}

public str toolTip()
{
    str     ret;

    switch (element.getModuleCached())
    {
        case FactureModule_RU::Cust :
            super();
            break;

        case FactureModule_RU::Vend :
            ret = "@SYS14048" + "@GLS111192" + strFmt("@SYS26868", salesBookTrans.AccountNum, salesBookTrans.AccountName);
            break;

        default:
    }

    return ret;
}

