public class SalesBookVATProcessLog_RU extends FormRun
{
    #define.fieldRecIdName('RecId')
    #define.Colon(": ")

    SalesBookTrans_RU       salesBookTrans;
    ToDate                  toDate;
    FromDate                fromDate;

    QueryBuildRange         qbrLogTableId;
    QueryBuildRange         qbrLogTransId;

    BookStructVATAmounts_RU processedAmounts;

