FactureModule_RU getModuleCached()
{
    FactureModule_RU    module;
    if (moduleCache.exists(salesBookVATProcessLogTrans.RecId))
    {
        module = moduleCache.lookup(salesBookVATProcessLogTrans.RecId);
    }
    else
    {
        module = salesBookVATProcessLogTrans.factureModule();
        moduleCache.insert(salesBookVATProcessLogTrans.RecId, module);
    }

    return module;
}

public void init()
{
    SalesBookTable_RU           bookTable;
    RestoredVATLogTransOper_RU  restoredVATLogTransOper;
    FactureJour_RU              factureJour;
    Common                      record = this.args().record();

    if (record)
    {
        switch (record.TableId)
        {
            case tableNum(SalesBookTrans_RU) :
                salesBookTrans  = record;
                bookTable       = SalesBookTable_RU::find(salesBookTrans.SalesBookTable_RU);
                fromDate        = bookTable.SalesPurchBookTable_RU::getPreviousBook().ClosingDate;

                if (fromDate)
                {
                    fromDate += 1;
                }

                toDate    = bookTable.ClosingDate;
                break;

            case tableNum(RestoredVATLogTransOper_RU) :
                restoredVATLogTransOper = record;
                mapRecIdRange = Map::create(restoredVATLogTransOper.salesBookVATProcessLog());
                break;

            case tableNum(FactureJour_RU) :
                factureJour = record;
                mapRecIdRange = Map::create(factureJour.bookVATProcessLog(ModuleCustVend::Cust));
                break;
        }
    }

    if ( ! toDate)
    {
        toDate = maxDate() - 1;
    }

    moduleCache = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Enum);
    vatCache    = new Map(Types::Container, Types::Class);

    super();
}

display SalesBookVATRestoringType_RU restoringType()
{
    return salesBookVATProcessLogTrans.ProcessingType == SalesBookProcessingType_RU::VATRestoration ? salesBookVATProcessLogTransOper.RestoringType : -1;
}

public void run()
