    super();

    this.initFromSalesBookTrans();

    element.setRecIdRange(this.table(), this.query());
}

private void initFromSalesBookTrans()
{
    Query                query;
    QueryBuildDataSource qbds;

    if (! salesBookTrans)
    {
        return;
    }
    query = this.query();

    qbds = query.dataSourceTable(tableNum(SalesBookVATProcessLogTable_RU));

    qbds = qbds.addDataSource(tableNum(SalesBookVATProcessLogTransOper_RU));
    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.relations(true);

    qbds = qbds.addDataSource(tableNum(SalesBookVATProcessLogTrans_RU));
    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.relations(true);

    qbds.addDynalink(fieldNum(SalesBookVATProcessLogTrans_RU, FactureId),       salesBookTrans, fieldNum(SalesBookTrans_RU, FactureId));
    qbds.addDynalink(fieldNum(SalesBookVATProcessLogTrans_RU, PaymentRecIdRef), salesBookTrans, fieldNum(SalesBookTrans_RU, PaymentRecIdRef));
    qbds.addDynalink(fieldNum(SalesBookVATProcessLogTrans_RU, PaymentCompany),  salesBookTrans, fieldNum(SalesBookTrans_RU, PaymentCompany));
}

