public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(SalesBookVATProcessLogTrans_RU));
    qbds = qbds.addDataSource(tableNum(SalesBookVATProcessLogTransOper_RU));
    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.relations(false);
    qbds.addLink(fieldNum(SalesBookVATProcessLogTrans_RU, RecId),
                 fieldNum(SalesBookVATProcessLogTransOper_RU, RefRecId));
    qbrLogTableId = qbds.addRange(fieldNum(SalesBookVATProcessLogTransOper_RU, LogTableRefRecId));

    element.setRecIdRange(this.table(), this.query());
}

public int active()
{
    int ret = super();

    if (ret)
    {
        qbrLogTransId.value(queryValue(salesBookVATProcessLogTrans.RecId));
        salesBookVATProcessLogTransOper_ds.executeQuery();

        if (vatCache.exists([salesBookVATProcessLogTrans.RecId, salesBookVATProcessLogTable.RecId]))
        {
            processedAmounts = vatCache.lookup([salesBookVATProcessLogTrans.RecId, salesBookVATProcessLogTable.RecId]);
        }
        else
        {
            if (salesBookVATProcessLogTable.ActionType == SalesBookVATProcessCancelType_RU::Process)
            {
                processedAmounts = SalesBookVATProcessLogTrans_RU::getLogAmounts(salesBookVATProcessLogTrans.RecId, salesBookVATProcessLogTable.RecId);
            }
            else
            {
                processedAmounts = SalesBookVATProcessLogTrans_RU::getLogAmounts(salesBookVATProcessLogTrans.RecId, salesBookVATProcessLogTable.RecId, true, false);
            }
            vatCache.insert([salesBookVATProcessLogTrans.RecId, salesBookVATProcessLogTable.RecId], processedAmounts);
        }

        element.setLabels();
        element.setContragentVisible();
    }

    return ret;
}

public str toolTip()
{
    str ret;

    FactureModule_RU module = element.getModuleCached();

    switch (module)
    {
        case FactureModule_RU::Cust :
            ret = super();
            break;
        case FactureModule_RU::Vend :
            ret = "@SYS14048" + #Colon +
                  strFmt("@SYS26868",
                         salesBookVATProcessLogTrans.AccountNum,
                         salesBookVATProcessLogTrans.AccountName);
            break;
    }

    return ret;
}

public void jumpRef()
{
    FactureModule_RU module = element.getModuleCached();

    switch (module)
