    QueryBuildDataSource    qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(SalesBookVATProcessLogTransOper_RU));
    qbrLogTransId = qbds.addRange(fieldNum(SalesBookVATProcessLogTransOper_RU, RefRecId));

    qbr = findOrCreateRange_W(qbds, fieldNum(SalesBookVATProcessLogTransOper_RU, Correct));
