void amountModified(TmpSalesBookVATProcessLogTransOper_RU _tmpSalesBookVATProcessLogTransOper,
                    TmpSalesBookVATProcessLogTransOper_RU _tmpSalesBookVATProcessLogTransOperOrig)
{
    BookStructSmallVATAmounts_RU    operCurrentAmountsOrig = new BookStructSmallVATAmounts_RU();

    try
    {
        bookProcessVATEngine.setCursors(tmpSalesBookVATProcessLogTrans, _tmpSalesBookVATProcessLogTransOper);

        operCurrentAmountsOrig.addAmounts(_tmpSalesBookVATProcessLogTransOperOrig.AmountLiableToVAT + _tmpSalesBookVATProcessLogTransOperOrig.TaxAmount,
                                          _tmpSalesBookVATProcessLogTransOperOrig.AmountLiableToVAT,
                                          _tmpSalesBookVATProcessLogTransOperOrig.TaxAmount);

        bookProcessVATEngine.operAmountModified(_tmpSalesBookVATProcessLogTransOper.AmountLiableToVAT,
                                                operCurrentAmountsOrig,
                                                _tmpSalesBookVATProcessLogTransOperOrig.Percent);

        this.updateLedgerAccounts();
    }
    catch (Exception::Error)
    {
        tmpSalesBookVATProcessLogTransOper_Ds.reread();
    }
}

void deleteData()
{
    boolean bEnableBtn;

    if (Box::yesNo("@SYS9461", DialogButton::No) != DialogButton::Yes)
    {
        return;
    }

    this.deleteDataFromDS();

    tmpSalesBookVATProcessLogTrans_ds.research();

    bEnableBtn = tmpSalesBookVATProcessLogTrans ? true : false;

    this.enableButton(bEnableBtn);
    btnCreateOperation.enabled(bEnableBtn);
}

protected void deleteDataFromDS()
{
    delete_from tmpSalesBookVATProcessLogTransOper;
    delete_from tmpSalesBookVATProcessLogTrans;

    bookProcessVATEngine = new BookProcessVATEngine_RU();
}

