void processData()
{
    BookPost_Sales_RU bookPost;
    TmpSalesBookVATProcessLogTrans_RU       logTrans;
    TmpSalesBookVATProcessLogTransOper_RU   logTransOper;

    if ( ! bookDataCalc || ! bookProcessVATEngine)
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    logTrans.setTmpData(tmpSalesBookVATProcessLogTrans);
    logTransOper.setTmpData(tmpSalesBookVATProcessLogTransOper);

    bookPost = BookPost_Sales_RU::newFromVATProcess(salesBookVATProcessCancelType,
                                                    logTrans,
                                                    logTransOper);
    if (bookPost.prompt())
    {
        try
        {
            bookPost.runOperation();
        }
        catch // to research data source
        {
            exceptionTextFallThrough();
        }
    }

    tmpSalesBookVATProcessLogTrans.setTmpData(logTrans);
    tmpSalesBookVATProcessLogTransOper.setTmpData(logTransOper);

    tmpSalesBookVATProcessLogTrans_DS.research();

    this.enableButton(tmpSalesBookVATProcessLogTrans ? true : false);
}

void selectData()
{
    TmpSalesBookVATProcessLogTrans_RU       logTrans;
    TmpSalesBookVATProcessLogTransOper_RU   logTransOper;

    this.deleteDataFromDS();

    bookDataCalc = BookDataCalc_Sales_Export_RU::newFromVATProcess(salesBookVATProcessCancelType);

    if (bookDataCalc.prompt())
    {
        bookDataCalc.runOperation();

        // asynchronous data fetch
        element.setTimeoutEx(formMethodStr(SalesBookVATProcess_RU, setDataBuffers));

        logTrans.setTmpData(bookDataCalc.parmProcessLogTrans());
        logTransOper.setTmpData(bookDataCalc.parmProcessLogTransOper());

        bookProcessVATEngine = BookProcessVATEngine_RU::create(logTrans, logTransOper);

        select firstonly logTrans;
        this.enableButton(logTrans ? true : false);
    }
}

void setAccessByDS()
{
    boolean bAccess        = false;
    boolean fieldsEditable = false;

    if (salesBookVATProcessCancelType == SalesBookVATProcessCancelType_RU::Process)
    {
        [bAccess, fieldsEditable] = tmpSalesBookVATProcessLogTrans.SalesPurchBookDataMap_RU::isEditable(ModuleCustVend::Cust);

        if (tmpSalesBookVATProcessLogTrans.TransType       == SalesPurchBookTransType_RU::Facture           &&
            (tmpSalesBookVATProcessLogTrans.ProcessingType == SalesBookProcessingType_RU::VATZero           ||
             tmpSalesBookVATProcessLogTrans.ProcessingType == SalesBookProcessingType_RU::VATZeroCorrection ||
             tmpSalesBookVATProcessLogTrans.ProcessingType == SalesBookProcessingType_RU::OverdueConfirmation))
        {
            bAccess        = false;
            fieldsEditable = false;
        }

        btnCreateOperation.enabled(bAccess);
    }

    if (fieldsEditable)
    {
        allowEditFieldsOnFormDS_W(tmpSalesBookVATProcessLogTransOper_ds, false);
        tmpSalesBookVATProcessLogTransOper_ds.object(fieldNum(TmpSalesBookVATProcessLogTransOper_RU, SalesBookVATProcessParameters_RU)).allowEdit(true);
        tmpSalesBookVATProcessLogTransOper_ds.object(fieldNum(TmpSalesBookVATProcessLogTransOper_RU, LedgerDimension)).allowEdit(true);
        tmpSalesBookVATProcessLogTransOper_ds.object(fieldNum(TmpSalesBookVATProcessLogTransOper_RU, OffsetLedgerDimension)).allowEdit(true);
    }
    else
    {
        allowEditFieldsOnFormDS_W(tmpSalesBookVATProcessLogTransOper_ds, bAccess);
        tmpSalesBookVATProcessLogTransOper_ds.allowDelete(bAccess);
    }

    if (tmpSalesBookVATProcessLogTrans.TransType       == SalesPurchBookTransType_RU::Invoice           &&
        (tmpSalesBookVATProcessLogTrans.ProcessingType == SalesBookProcessingType_RU::VATZero           ||
         tmpSalesBookVATProcessLogTrans.ProcessingType == SalesBookProcessingType_RU::VATZeroCorrection ||
         tmpSalesBookVATProcessLogTrans.ProcessingType == SalesBookProcessingType_RU::OverdueConfirmation))
    {
        tmpSalesBookVATProcessLogTransOper_ds.object(fieldNum(TmpSalesBookVATProcessLogTransOper_RU, LedgerDimension)).allowEdit(false);
        tmpSalesBookVATProcessLogTransOper_ds.object(fieldNum(TmpSalesBookVATProcessLogTransOper_RU, OffsetLedgerDimension)).allowEdit(false);
    }

    tmpSalesBookVATProcessLogTransOper_ds.object(fieldNum(TmpSalesBookVATProcessLogTransOper_RU, TaxCode)).allowEdit(false);
}

void setContragentVisible()
{
    FactureModule_RU    module;

    groupVendor.visible(false);
    groupCustomer.visible(false);

    btnFactureJournalCust.visible(false);
    btnFactureJournalVend.visible(false);

    if (tmpSalesBookVATProcessLogTrans.RecId)
    {
        module = element.getModuleCached();

        switch (module)
        {
            case FactureModule_RU::Cust                    :
                groupCustomer.visible(true);
                btnFactureJournalCust.visible(true);
                break;
            case FactureModule_RU::Vend                    :
                vendorAccountNum.value(tmpSalesBookVATProcessLogTrans.AccountNum);
                vendTable_DS.executeQuery();
                groupVendor.visible(true);
                btnFactureJournalVend.visible(true);
                break;
            default                                        :
                throw error(Error::wrongUseOfFunction(funcName()));
        }
    }
    else
    {
        groupCustomer.visible(true);
    }
}

