void updateLedgerAccounts()
{
    LedgerDimensionDefaultAccount       defaultAccount, offsetDefaultAccount;
    SalesBookVATProcessParameters_RU    parameters    = SalesBookVATProcessParameters_RU::find(tmpSalesBookVATProcessLogTransOper.SalesBookVATProcessParameters_RU);
    SalesBookVATLedgerPosting_RU        ledgerPosting = SalesBookVATLedgerPosting_RU::ledgerPosting(tmpSalesBookVATProcessLogTransOper.TaxCode,
                                                                                                    tmpSalesBookVATProcessLogTransOper.SalesBookVATProcessParameters_RU);

    if (tmpSalesBookVATProcessLogTransOper.TaxAmount &&
        tmpSalesBookVATProcessLogTransOper_ds.object(fieldNum(TmpSalesBookVATProcessLogTransOper_RU, LedgerDimension)).allowEdit())
    {
        [defaultAccount, offsetDefaultAccount] = SalesBookVATProcessParameters_RU::calcLedgerDimensions(tmpSalesBookVATProcessLogTransOper.TaxCode,
                                                                                                        tmpSalesBookVATProcessLogTransOper.SalesBookVATProcessParameters_RU,
                                                                                                        tmpSalesBookVATProcessLogTrans.TransType,
                                                                                                        tmpSalesBookVATProcessLogTrans.InvoiceRecIdRef,
                                                                                                        tmpSalesBookVATProcessLogTrans.PaymentRecIdRef,
                                                                                                        tmpSalesBookVATProcessLogTrans.InvoiceCompany,
                                                                                                        tmpSalesBookVATProcessLogTrans.PaymentCompany);

        tmpSalesBookVATProcessLogTransOper.LedgerDimension       = ledgerPosting.LedgerDimension        ? ledgerPosting.LedgerDimension       : defaultAccount;
        tmpSalesBookVATProcessLogTransOper.OffsetLedgerDimension = ledgerPosting.OffsetLedgerDimension  ? ledgerPosting.OffsetLedgerDimension : offsetDefaultAccount;
        tmpSalesBookVATProcessLogTransOper.TransTxt              = parameters.Description;
    }
    else
    {
        tmpSalesBookVATProcessLogTransOper.LedgerDimension       = 0;
        tmpSalesBookVATProcessLogTransOper.OffsetLedgerDimension = 0;
        tmpSalesBookVATProcessLogTransOper.TransTxt              = '';
    }

    tmpSalesBookVATProcessLogTransOper.DeriveDim              = parameters.DeriveDim;
    tmpSalesBookVATProcessLogTransOper.DefaultDimensionPosted = parameters.DeriveDim ? parameters.DefaultDimension : tmpSalesBookVATProcessLogTransOper.DefaultDimension;

    tmpSalesBookVATProcessLogTransOper_DS.active();
}

