public void jumpRef()
{
    FactureModule_RU module = element.getModuleCached();

    switch (module)
    {
        case FactureModule_RU::Cust :
            super();
            break;
        case FactureModule_RU::Vend :
            vendTable_DS.object(fieldNum(VendTable, AccountNum)).jumpRef();
            break;
    }
}

#define.Colon(": ")
public str toolTip()
{
    str ret;

    FactureModule_RU module = element.getModuleCached();

    switch (module)
    {
        case FactureModule_RU::Cust :
            ret = super();
            break;

        case FactureModule_RU::Vend :
            ret = "@SYS14048" + #Colon +
                  strFmt("@SYS26868",
                         tmpSalesBookVATProcessLogTrans.AccountNum,
                         tmpSalesBookVATProcessLogTrans.AccountName);
            break;
    }

    return ret;
}

