display UnitQty operSelectedQty(TmpSalesBookVATProcessLogTransOper_RU _tmpSalesBookVATProcessLogTransOper)
{
    UnitQty factureQty;
    if (this.operSelectedAmountInclVAT(_tmpSalesBookVATProcessLogTransOper))
    {
        factureQty = this.operNotUsedQty(_tmpSalesBookVATProcessLogTransOper);
    }
    return factureQty;
}

display UnitQty operUsedQty(TmpSalesBookVATProcessLogTransOper_RU _tmpSalesBookVATProcessLogTransOper)
{
    UnitQty factureQty;
    Factor  factor;
    Amount  factureAmount;
    factureAmount = this.operFactureAmountInclVAT(_tmpSalesBookVATProcessLogTransOper);
    if (factureAmount)
    {
        factureQty = this.operFactureQty(_tmpSalesBookVATProcessLogTransOper) * _tmpSalesBookVATProcessLogTransOper.Percent / 100;
        factor = this.operUsedAmountInclVAT(_tmpSalesBookVATProcessLogTransOper) / factureAmount;
    }
    return factureQty * factor;
}

display UnitQty operSettledQty(TmpSalesBookVATProcessLogTransOper_RU _tmpSalesBookVATProcessLogTransOper)
{
    UnitQty factureQty;
    Factor  factor;
    Amount  factureAmount;
    factureAmount = this.operFactureAmountInclVAT(_tmpSalesBookVATProcessLogTransOper);
    if (factureAmount)
    {
        factureQty = this.operFactureQty(_tmpSalesBookVATProcessLogTransOper) * _tmpSalesBookVATProcessLogTransOper.Percent / 100;
        factor = this.operSettledAmountInclVAT(_tmpSalesBookVATProcessLogTransOper) / factureAmount;
    }
    return factureQty * factor;
}

display UnitQty operFactureQty(TmpSalesBookVATProcessLogTransOper_RU _tmpSalesBookVATProcessLogTransOper)
{
    UnitQty factureQty;
    bookProcessVATEngine.setCursors(tmpSalesBookVATProcessLogTrans, _tmpSalesBookVATProcessLogTransOper);
    factureQty =  bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::FacturedAmount).qty();
    return factureQty ? factureQty : 1;
}

edit UnitQty qty(boolean _set, TmpSalesBookVATProcessLogTransOper_RU _tmpSalesBookVATProcessLogTransOper, UnitQty _qty)
{
    UnitQty                       lineQty;
    Factor                        factor;
    BookStructSmallVATAmounts_RU  operSettledAmounts, operUsedAmounts;

    bookProcessVATEngine.setCursors(tmpSalesBookVATProcessLogTrans, _tmpSalesBookVATProcessLogTransOper);
    lineQty =  bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::FacturedAmount).qty() *
               _tmpSalesBookVATProcessLogTransOper.Percent / 100;

    if (_set && lineQty)
    {
        factor = _qty /lineQty ;
        _tmpSalesBookVATProcessLogTransOper.Percent = _tmpSalesBookVATProcessLogTransOper.Percent * factor;
        operSettledAmounts = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::SettledAmount);
        operUsedAmounts    = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::UsedAmount);
        _tmpSalesBookVATProcessLogTransOper.AmountLiableToVAT = CurrencyExchangeHelper::amount((operSettledAmounts.amountLiableToVAT() - operUsedAmounts.amountLiableToVAT()) * tmpSalesBookVATProcessLogTransOper.Percent / 100);
        element.amountModified(_tmpSalesBookVATProcessLogTransOper, _tmpSalesBookVATProcessLogTransOper.orig());
    }

    return bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::FacturedAmount).qty() *
           _tmpSalesBookVATProcessLogTransOper.Percent / 100;
}

public int active()
{
    int     ret = super();

    if (ret)
    {
        tmpSalesBookVATProcessLogTransOper_DS.allowDelete(tmpSalesBookVATProcessLogTransOper.RecId && tmpSalesBookVATProcessLogTransOper.AllowDelete);

        element.setAccessByDS();
    }

    return ret;
}

public boolean validateDelete()
{
    boolean                                 ret;
    BookStructSmallVATAmounts_RU            currentAmounts;

    ret = super();

    if (ret)
    {
        if (tmpSalesBookVATProcessLogTransOper.AllowDelete == NoYes::No)
        {
            ret = false;
        }
        else
        {
            currentAmounts = new BookStructSmallVATAmounts_RU();

            bookProcessVATEngine.setCursors(tmpSalesBookVATProcessLogTrans, tmpSalesBookVATProcessLogTransOper);

            currentAmounts.addAmounts(tmpSalesBookVATProcessLogTransOper.AmountLiableToVAT + tmpSalesBookVATProcessLogTransOper.TaxAmount,
                                      tmpSalesBookVATProcessLogTransOper.AmountLiableToVAT,
                                      tmpSalesBookVATProcessLogTransOper.TaxAmount);

            bookProcessVATEngine.subOperAmounts(BookVATAmountType_RU::CurrentAmount, currentAmounts, tmpSalesBookVATProcessLogTransOper.Percent);
        }
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (SalesBookVATProcessParameters_RU::find(tmpSalesBookVATProcessLogTransOper.SalesBookVATProcessParameters_RU).IncludeInBook)
    {
        LedgerDimension.parmPostingType(LedgerPostingType::Tax);
        GroupLedgerOperation_LedgerAccount.parmPostingType(LedgerPostingType::Tax);
    }
    else
    {
        LedgerDimension.parmPostingType(LedgerPostingType::None);
        GroupLedgerOperation_LedgerAccount.parmPostingType(LedgerPostingType::None);
    }

    super(_formControl, _filterStr);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (SalesBookVATProcessParameters_RU::find(tmpSalesBookVATProcessLogTransOper.SalesBookVATProcessParameters_RU).IncludeInBook)
    {
        OffsetLedgerDimension.parmPostingType(LedgerPostingType::Tax);
        GroupLedgerOperation_OffsetLedgerAccount.parmPostingType(LedgerPostingType::Tax);
    }
    else
    {
        OffsetLedgerDimension.parmPostingType(LedgerPostingType::None);
        GroupLedgerOperation_OffsetLedgerAccount.parmPostingType(LedgerPostingType::None);
    }

    super(_formControl, _filterStr);
}

public void modified()
{
    BookStructSmallVATAmounts_RU  operSettledAmounts, operUsedAmounts;

    super();

    bookProcessVATEngine.setCursors(tmpSalesBookVATProcessLogTrans, tmpSalesBookVATProcessLogTransOper);
    operSettledAmounts = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::SettledAmount);
    operUsedAmounts    = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::UsedAmount);

    tmpSalesBookVATProcessLogTransOper.AmountLiableToVAT = CurrencyExchangeHelper::amount((operSettledAmounts.amountLiableToVAT() - operUsedAmounts.amountLiableToVAT()) * tmpSalesBookVATProcessLogTransOper.Percent / 100);

    element.amountModified(tmpSalesBookVATProcessLogTransOper, tmpSalesBookVATProcessLogTransOper.orig());

    tmpSalesBookVATProcessLogTransOper_DS.write();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(SalesBookVATProcessParameters_RU), _formReferenceControl);
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    Common                  ret;

    // Do not call super as we're providing our own custom lookup logic.
    // ret = super(_formReferenceControl);

    sysTableLookup.addLookupfield(fieldNum(SalesBookVATProcessParameters_RU, OperationCode));
    sysTableLookup.addLookupfield(fieldNum(SalesBookVATProcessParameters_RU, ProcessingType));
    sysTableLookup.addLookupfield(fieldNum(SalesBookVATProcessParameters_RU, Description));

    queryBuildDataSource = query.addDataSource(tableNum(SalesBookVATProcessParameters_RU));

    findOrCreateRange_W(queryBuildDataSource,
                        fieldNum(SalesBookVATProcessParameters_RU, ProcessingType),
                        queryValue(tmpSalesBookVATProcessLogTrans.ProcessingType));

    sysTableLookup.parmQuery(query);
    ret = sysTableLookup.performFormLookup();

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (SalesBookVATProcessParameters_RU::find(tmpSalesBookVATProcessLogTransOper.SalesBookVATProcessParameters_RU).ProcessingType !=
            tmpSalesBookVATProcessLogTrans.ProcessingType)
        {
            ret = checkFailed(strFmt("@SYS108459",
                                     fieldStr(TmpSalesBookVATProcessLogTransOper_RU, SalesBookVATProcessParameters_RU),
                                     tmpSalesBookVATProcessLogTrans.ProcessingType));
        }
    }

    return ret;
}

public void modified()
{
    BookStructSmallVATAmounts_RU    operSettledAmounts, operUsedAmounts;
    TaxAmount                       taxAmountNotUsed;

    super();

    bookProcessVATEngine.setCursors(tmpSalesBookVATProcessLogTrans, tmpSalesBookVATProcessLogTransOper);
    operSettledAmounts = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::SettledAmount);
    operUsedAmounts    = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::UsedAmount);
    taxAmountNotUsed   = operSettledAmounts.taxAmountVAT() - operUsedAmounts.taxAmountVAT();

    tmpSalesBookVATProcessLogTransOper.AmountLiableToVAT =  taxAmountNotUsed ?
                                                            CurrencyExchangeHelper::amount((operSettledAmounts.amountLiableToVAT() - operUsedAmounts.amountLiableToVAT()) * tmpSalesBookVATProcessLogTransOper.TaxAmount / taxAmountNotUsed) : 0;

    element.amountModified(tmpSalesBookVATProcessLogTransOper, tmpSalesBookVATProcessLogTransOper.orig());

    tmpSalesBookVATProcessLogTransOper_DS.write();
}

public boolean validate()
{
    boolean ret = super();

    if (ret)
    {
        if (tmpSalesBookVATProcessLogTransOper.TransDate < tmpSalesBookVATProcessLogTrans.SalesBookTransDate)
        {
            error("@GLS104952");
            ret = false;
        }
    }

    return ret;
}

