void clicked()
{
    TmpSalesBookVATProcessLogTransOper_RU localTmpSalesBookVATProcessLogTransOper;
    BookStructSmallVATAmounts_RU          avaliableAmounts;

    void createLine(TmpSalesBookVATProcessLogTransOper_RU _tmpSalesBookVATProcessLogTransOper)
    {
        tmpSalesBookVATProcessLogTransOper.data(_tmpSalesBookVATProcessLogTransOper.data());

        bookProcessVATEngine.setCursors(tmpSalesBookVATProcessLogTrans, tmpSalesBookVATProcessLogTransOper);
        avaliableAmounts = bookProcessVATEngine.getOperAmounts(BookVATAmountType_RU::AvaliableAmount);
        bookProcessVATEngine.operAmountModified(avaliableAmounts.amountLiableToVAT());
        tmpSalesBookVATProcessLogTransOper.AllowDelete = NoYes::Yes;
        tmpSalesBookVATProcessLogTransOper.insert();
    }

    super();

    if (tmpSalesBookVATProcessLogTransOper_DS.anyMarked())
    {
        localTmpSalesBookVATProcessLogTransOper = tmpSalesBookVATProcessLogTransOper_DS.getFirst(true, false);

        while (localTmpSalesBookVATProcessLogTransOper)
        {
            createLine(localTmpSalesBookVATProcessLogTransOper);
            localTmpSalesBookVATProcessLogTransOper = tmpSalesBookVATProcessLogTransOper_DS.getNext();
        }
    }
    else
    {
        localTmpSalesBookVATProcessLogTransOper = tmpSalesBookVATProcessLogTransOper_QR.get(tableNum(TmpSalesBookVATProcessLogTransOper_RU));
        createLine(localTmpSalesBookVATProcessLogTransOper);
    }

    element.updateLedgerAccounts();

    tmpSalesBookVATProcessLogTransOper_DS.write();
    tmpSalesBookVATProcessLogTransOper_DS.research();
}
