public void adjustDesign()
{
    boolean canPrint = false;
    boolean canEditTransDate;
    SalesComplementaryInvoiceHeader complementaryInvoice;

    if (SalesComplementaryInvoiceHeader.ComplementaryType == InvoiceComplementaryType_BR::Price)
    {
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, AdditionalPovertyFundAmount)).visible(false);
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, AdditionalICMSAmount)).visible(false);
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, AdditionalIPIAmount)).visible(false);
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, OriginalLineAmount)).visible(true);
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, Amount)).visible(true);
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, CorrectedAmount)).visible(true);
        AllocateTaxAmountButton.enabled(false);
    }
    else
    {
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, AdditionalPovertyFundAmount)).visible(true);	
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, AdditionalICMSAmount)).visible(true);
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, AdditionalIPIAmount)).visible(true);
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, OriginalLineAmount)).visible(true);
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, Amount)).visible(false);
        SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, CorrectedAmount)).visible(false);
        AllocateTaxAmountButton.enabled(true);
    }

    switch (SalesComplementaryInvoiceHeader.Status)
    {
        case ComplementaryInvoiceStatus::Open:
            DialogCancelButton.enabled(false);
            PostButton.enabled(true);
            VoucherButton.enabled(false);
            SalesComplementaryInvoiceHeader_ds.allowEdit(true);
            SalesComplementaryInvoiceHeader_ds.allowDelete(true);

            if (SalesComplementaryInvoiceHeader.CanceledInvoice)
            {
                SalesComplementaryInvoiceLine_ds.allowEdit(false);
                SalesComplementaryInvoiceLine_ds.allowDelete(false);
            }
            else
            {
                SalesComplementaryInvoiceLine_ds.allowEdit(true);
                SalesComplementaryInvoiceLine_ds.allowDelete(true);
            }

            if (SalescomplementaryInvoiceHeader.PaymSched)
            {
                buttonPaymentSched.enabled(true);
            }
            else
            {
                buttonPaymentSched.enabled(false);
            }
            GroupFinancialDimensionLine.allowEdit(true);
            if (BrazilParameters::find().ChronologicalInvoiceNumbering &&
                FiscalDocumentType_BR::findRecId(SalesComplementaryInvoiceHeader.FiscalDocumentType_BR).InvoiceIssueDate)
            {
                canEditTransDate = false;
            }
            else
            {
                canEditTransDate = true;
            }
            SalesComplementaryInvoiceHeader_ds.object(fieldNum(SalesComplementaryInvoiceHeader, DocumentDate)).allowEdit(canEditTransDate);
            break;

        case ComplementaryInvoiceStatus::Canceled:
        case ComplementaryInvoiceStatus::CancelInProgress:
            DialogCancelButton.enabled(false);
            PostButton.enabled(false);
            VoucherButton.enabled(true);
            SalesComplementaryInvoiceHeader_ds.allowEdit(false);
            SalesComplementaryInvoiceHeader_ds.allowDelete(false);
            SalesComplementaryInvoiceLine_ds.allowEdit(false);
            SalesComplementaryInvoiceLine_ds.allowDelete(false);
            buttonPaymentSched.enabled(false);
            GroupFinancialDimensionLine.allowEdit(false);
            break;

        case ComplementaryInvoiceStatus::Posted:
            if (SalesComplementaryInvoiceHeader.CanceledInvoice)
            {
                DialogCancelButton.enabled(false);
            }
            else
            {
                DialogCancelButton.enabled(true);
            }
            PostButton.enabled(false);
            VoucherButton.enabled(true);
            SalesComplementaryInvoiceHeader_ds.allowEdit(false);
            SalesComplementaryInvoiceHeader_ds.allowDelete(false);
            SalesComplementaryInvoiceLine_ds.allowEdit(false);
            SalesComplementaryInvoiceLine_ds.allowDelete(false);
            buttonPaymentSched.enabled(false);
            GroupFinancialDimensionLine.allowEdit(false);
            break;
    }

    if (CustPaymModeTable.PaymMode != SalescomplementaryInvoiceHeader.CustPaymModeTable)
    {
        custPaymModeTable = CustPaymModeTable::find(SalescomplementaryInvoiceHeader.CustPaymModeTable);
    }
    buttonSetupPaymFormat.enabled(custPaymModeTable.ExportOnInvoice);

    for (complementaryInvoice = getFirstSelection(SalesComplementaryInvoiceHeader_DS);
    complementaryInvoice && !canPrint;
    complementaryInvoice = SalesComplementaryInvoiceHeader_DS.getNext())
    {
        if (complementaryInvoice.Status != ComplementaryInvoiceStatus::Open)
        {
            canPrint = true;
        }
    }

    DocumentGroup.enabled(canPrint);
}

public void deleteCalculatedTaxData()
{
    TaxUncommitted::deleteForDocumentHeader(tableNum(SalesComplementaryInvoiceHeader), SalesComplementaryInvoiceHeader.RecId);
    CustPaymSched::deleteCustPaymSched(SalesComplementaryInvoiceHeader.TableId, SalesComplementaryInvoiceHeader.RecId);
}

public void init()
{
    super();

    if (element.args().record())
    {
        SalesComplementaryInvoiceHeader_ds.queryBuildDataSource().clearDynalinks();
    }
}

public void printInvoice(PrintCopyOriginal _action)
{
    GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceBR00052, funcName());

    SalesComplementaryInvoiceHeader complementaryInvoice;
    RecordSortedList                journalList  = new RecordSortedList(tableNum(FiscalDocument_BR));
    FiscalDocument_BR               fiscalDocument;
    Args                            parameters = new Args();

    for (complementaryInvoice = getFirstSelection(SalesComplementaryInvoiceHeader_DS);
    complementaryInvoice;
    complementaryInvoice = SalesComplementaryInvoiceHeader_DS.getNext())
    {
        fiscalDocument = FiscalDocument_BR::findByRef(complementaryInvoice.TableId, complementaryInvoice.RecId);
        if (fiscalDocument)
        {
            journalList.ins(fiscalDocument);
        }
    }

    while (journalList.next(fiscalDocument))
    {
        parameters.record(fiscalDocument);
        parameters.parmEnum(_action);
        parameters.parmEnumType(enumNum(PrintCopyOriginal));
        new MenuFunction(menuitemOutputStr(SalesComplementaryInvoice), MenuItemType::Output).run(parameters);
    }
}

public void setTitleText()
{
    if (SalesComplementaryInvoiceHeader.FiscalDocumentNumber)
    {
        HeaderTitle.text(strFmt('%1 : %2 - %3', SalesComplementaryInvoiceHeader.FiscalDocumentNumber,
                                                SalesComplementaryInvoiceHeader.CustTable,
                                                CustTable::find(SalesComplementaryInvoiceHeader.CustTable).name()));
    }
    else
    {
        HeaderTitle.text(strFmt('%1 - %2', SalesComplementaryInvoiceHeader.CustTable,
                                                CustTable::find(SalesComplementaryInvoiceHeader.CustTable).name()));
    }
}

private boolean enableAdditionalPovertyFundAmount()
{
    CustVendCFOPId_BR           cfopId;
    CfopDirectionCheck_BR       cfopDirectionCheck;

    cfopId = CFOPTable_BR::find(SalesComplementaryInvoiceLine.CFOPTable_BR).CFOPId;
    cfopDirectionCheck = CfopDirectionCheck_BR::construct(cfopId);

    return !cfopDirectionCheck.isOtherState();
}

public int active()
{
    boolean enableAdditionalPovertyFundAmount;
    int ret;

    ret = super();

    enableAdditionalPovertyFundAmount = this.enableAdditionalPovertyFundAmount();
    SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, AdditionalPovertyFundAmount)).allowEdit(enableAdditionalPovertyFundAmount);
    SalesComplementaryInvoiceLine_ds.object(fieldNum(SalesComplementaryInvoiceLine, AdditionalPovertyFundAmount)).enabled(enableAdditionalPovertyFundAmount);

    return ret;
}

public void delete()
{
    element.deleteCalculatedTaxData();
    super();
}

public void write()
{
    boolean recreateTax;

    recreateTax = SalesComplementaryInvoiceLine.Amount != SalesComplementaryInvoiceLine.orig().Amount ||
                  SalesComplementaryInvoiceLine.AdditionalICMSAmount != SalesComplementaryInvoiceLine.orig().AdditionalICMSAmount ||
                  SalesComplementaryInvoiceLine.AdditionalIPIAmount != SalesComplementaryInvoiceLine.orig().AdditionalIPIAmount ||
                  SalesComplementaryInvoiceLine.TaxGroup != SalesComplementaryInvoiceLine.orig().TaxGroup ||
                  SalesComplementaryInvoiceLine.TaxItemGroup != SalesComplementaryInvoiceLine.orig().TaxItemGroup ||
                  SalesComplementaryInvoiceLine.AccountingDistributionTemplate != SalesComplementaryInvoiceLine.orig().AccountingDistributionTemplate;

    if (recreateTax)
    {
        element.deleteCalculatedTaxData();
    }

    super();
}

