void modified()
{
    super();

    salescomplementaryinvoiceheader.CashDiscPercent = CashDisc::find(salescomplementaryinvoiceheader.CashDisc).Percent;

    if (TaxParameters::find().CashDiscOnInvoice)
    {
        salescomplementaryinvoiceheader.CashDiscDate = dateNull();
        salescomplementaryinvoiceheader.calcCashDiscDate();
    }

    if (salescomplementaryinvoiceheader.CashDisc == '')
    {
        salescomplementaryinvoiceheader_ds.object(fieldNum(salescomplementaryinvoiceheader, CashDiscDate)).allowEdit(false);
        salescomplementaryinvoiceheader_ds.object(fieldNum(salescomplementaryinvoiceheader, CashDiscDate)).skip(true);
    }
    else
    {
        salescomplementaryinvoiceheader_ds.object(fieldNum(salescomplementaryinvoiceheader, CashDiscDate)).allowEdit(true);
        salescomplementaryinvoiceheader_ds.object(fieldNum(salescomplementaryinvoiceheader, CashDiscDate)).skip(false);
    }
}

