public void dialogClosed(xFormRun _formRun)
{
    #task

    super(_formRun);

    if (_formRun.closedOk())
    {
        SalesComplementaryInvoiceHeader cancelingDocument = SalesComplementaryInvoiceHeader.getCancelSalesComplInvoice();

        element.args().lookupRecord(cancelingDocument);
        SalesComplementaryInvoiceHeader_ds.research(false);
        element.task(#taskSwitchToLineDetailsView);
        element.adjustDesign();
    }
}

void clicked()
{
    SalesComplInvCalcTotals     complInvTotals;
    Args                        args;
    FormRun                     formRun;

    if (SalesComplementaryInvoiceHeader.Status != ComplementaryInvoiceStatus::Open)
    {
        args = new Args();
        args.caller(element);
        args.record(SalesComplementaryInvoiceHeader);

        formRun = new MenuFunction(menuitemDisplayStr(TaxTrans), MenuItemType::Display).create(args);
        formRun.run();
    }
    else
    {
        complInvTotals = new SalesComplInvCalcTotals(SalesComplementaryInvoiceHeader);
        complInvTotals.calc();
        Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkSalesComplInv), complInvTotals.tax(), SalesComplementaryInvoiceHeader);
    }
}

void clicked()
{
    CustVendPaymSched   custVendPaymSched;

    if (salesComplementaryInvoiceHeader.PaymSched && !CustPaymSched::exist(salesComplementaryInvoiceHeader.TableId,salesComplementaryInvoiceHeader.RecId))
    {
        custVendPaymSched = CustVendPaymSched::construct(SysModule::Cust, salesComplementaryInvoiceHeader);
        custVendPaymSched.copyPaymSched(PaymSched::find(salesComplementaryInvoiceHeader.PaymSched));
        salesComplementaryInvoiceHeader.createPaymentSched();
    }
    super();
}

