/// <summary>
/// Checks if advance invoice settled.
/// </summary>
/// <param name = "_common">Advance invoice.</param>
/// <returns>Returns true if cust invoice jour exists otherwise returns false.</returns>
public boolean advanceInvoiceSettled_W(Common _common)
{
    if (_common.TableId != tablenum(CustInvoiceTrans))
        return false;

    CustInvoiceTrans localCustInvoiceTrans = _common;

    return CustAdvanceInvoiceSettlement_W::existsCustInvoiceJour(localCustInvoiceTrans.custInvoiceJour());
}

/// <summary>
/// Updates a tree control which contain selected for copy operation lines.
/// </summary>
void updateTreeControl()
{
    if (TabPageTreeControl.isExpanded())
    {
        element.buildTreeControl();
    }
}

void buildTreeControl()
{
    int                 treeEntry;

    InvoiceId           invoiceId;
    SalesId             invoiceSalesId;

    PackingSlipId       packingSlipId;
    SalesId             packingSlipSalesId;

    QuotationIdBase     quotationId;
    SalesId             quotationSalesId;

    ConfirmId           confirmId;
    SalesId             confirmSalesId;

    ProjInvoiceId       projInvoiceId;
    SalesId             projInvoiceSalesId;

    SalesId             salesId;

    ItemId              itemId;
    ItemNameDisplay     itemName;
    SalesCategory       salesCategory;
    ItemFreeTxt         name;

    // <GEERU>
    Invoice4PaymId_RU   invoice4PaymId;
    SalesId             invoice4PaymSalesId;
    // </GEERU>
    CustInvoiceTrans        localCustInvoiceTrans;
    CustPackingSlipTrans    localCustPackingSlipTrans;
    CustQuotationTrans      localCustQuotationTrans;
    CustConfirmTrans        localCustConfirmTrans;
    SalesLine               localSalesLine;
    ProjInvoiceItem         localProjInvoiceItem;

    treeControl.deleteAll();

    while select tmpFrmVirtualLines order by Id, TransDate
    {
        switch (tmpFrmVirtualLines.TableNum)
        {
            case tableNum(CustInvoiceTrans):
                localCustInvoiceTrans = CustInvoiceTrans::findRecId(tmpFrmVirtualLines.RecordNo);
                if (invoiceId != localCustInvoiceTrans.InvoiceId || invoiceSalesId != localCustInvoiceTrans.SalesId )
                {
                    treeControl.expand(treeEntry);
                    treeEntry           = treeControl.addItem(0,
                                                              0,
                                                              new FormTreeItem(strFmt('%1 %2 %3 %4',
                                                                                      "@SYS12128",
                                                                                      localCustInvoiceTrans.InvoiceId,
                                                                                      "@SYS9694",
                                                                                      localCustInvoiceTrans.SalesId),
                                                              imageListAppl_Sales.image(#ImageInvoice)));
                    invoiceId           = localCustInvoiceTrans.InvoiceId;
                    invoiceSalesId      = localCustInvoiceTrans.SalesId;
                }
                itemName        = localCustInvoiceTrans.itemName();
                itemId          = localCustInvoiceTrans.ItemId;
                salesCategory   = localCustInvoiceTrans.SalesCategory;
                name            = localCustInvoiceTrans.Name;
                break;

            case tableNum(CustPackingSlipTrans):
                localCustPackingSlipTrans = CustPackingSlipTrans::findRecId(tmpFrmVirtualLines.RecordNo);
                if (packingSlipId != localCustPackingSlipTrans.PackingSlipId || packingSlipSalesId != localCustPackingSlipTrans.SalesId)
                {
                    treeControl.expand(treeEntry);
                    treeEntry           = treeControl.addItem(0,
                                                              0,
                                                              new FormTreeItem(strFmt('%1 %2 %3 %4',
                                                                                      "@SYS11549",
                                                                                      localCustPackingSlipTrans.PackingSlipId, "@SYS9694",
                                                                                      localCustPackingSlipTrans.SalesId),
                                                              imageListAppl_Sales.image(#ImagePackingSlip)));
                    packingSlipId       = localCustPackingSlipTrans.PackingSlipId;
                    packingSlipSalesId  = localCustPackingSlipTrans.SalesId;
                }
                itemName        = localCustPackingSlipTrans.itemName();
                itemId          = localCustPackingSlipTrans.ItemId;
                salesCategory   = localCustPackingSlipTrans.SalesCategory;
                name            = localCustPackingSlipTrans.Name;
                break;

            case tableNum(CustQuotationTrans):
                localCustQuotationTrans = CustQuotationTrans::findRecId(tmpFrmVirtualLines.RecordNo);
                if (quotationId != localCustQuotationTrans.QuotationId || quotationSalesId != localCustQuotationTrans.SalesId)
                {
                    treeControl.expand(treeEntry);
                    treeEntry           = treeControl.addItem(0,
                                                              0,
                                                              new FormTreeItem(strFmt('%1 %2 %3 %4',"@SYS5864",
                                                                                      localCustQuotationTrans.QuotationId,
                                                                                      "@SYS9694",
                                                                                      localCustQuotationTrans.SalesId),
                                                              imageListAppl_Sales.image(#ImageQuotation)));
                    quotationId         = localCustQuotationTrans.QuotationId;
                    quotationSalesId    = localCustQuotationTrans.SalesId;
                }
                itemName        = localCustQuotationTrans.itemName();
                itemId          = localCustQuotationTrans.ItemId;
                salesCategory   = localCustQuotationTrans.SalesCategory;
                name            = localCustQuotationTrans.Name;
                break;

            case tableNum(CustConfirmTrans):
                localCustConfirmTrans = CustConfirmTrans::findRecId(tmpFrmVirtualLines.RecordNo);
                if (confirmId != localCustConfirmTrans.ConfirmId || confirmSalesId != localCustConfirmTrans.SalesId)
                {
                    treeControl.expand(treeEntry);
                    treeEntry           = treeControl.addItem(0,
                                                              0,
                                                              new FormTreeItem(strFmt('%1 %2 %3 %4',
                                                                                      "@SYS12529",
                                                                                      localCustConfirmTrans.ConfirmId,
                                                                                      "@SYS9694",
                                                                                      localCustConfirmTrans.SalesId),
                                                              imageListAppl_Sales.image(#ImageConfirm)));
                    confirmId           = localCustConfirmTrans.ConfirmId;
                    confirmSalesId      = localCustConfirmTrans.SalesId;
                }
                itemName        = localCustConfirmTrans.itemName();
                itemId          = localCustConfirmTrans.ItemId;
                salesCategory   = localCustConfirmTrans.SalesCategory;
                name            = localCustConfirmTrans.Name;
                break;

            // <GEERU>
            case tableNum(CustInvoice4PaymTrans_RU):
                if (!countryRegion_RU)
                {
                    break;
                }

                custInvoice4PaymTrans_RU_ds.positionToRecord(CustInvoice4PaymTrans_RU::findRecId(tmpFrmVirtualLines.RecordNo));
                if (invoice4PaymId != custInvoice4PaymTrans_RU.Invoice4PaymId || invoice4PaymSalesId != custInvoice4PaymTrans_RU.SalesId )
                {
                    treeControl.expand(treeEntry);
                    treeEntry           = treeControl.addItem(0,0,new FormTreeItem(strFmt('%1 %2',"@GLS102245", custInvoice4PaymTrans_RU.Invoice4PaymId), imageListAppl_Sales.image(#ImageConfirm)));
                    invoice4PaymId      = custInvoice4PaymTrans_RU.Invoice4PaymId;
                    invoice4PaymSalesId = custInvoice4PaymTrans_RU.SalesId;
                }
                itemName        = custInvoice4PaymTrans_RU.itemName();
                itemId          = custInvoice4PaymTrans_RU.ItemId;
                salesCategory   = custInvoice4PaymTrans_RU.Category;
                name            = custInvoice4PaymTrans_RU.Name;
                break;
            // </GEERU>
            case tableNum(SalesLine):
                localSalesLine = SalesLine::findRecId(tmpFrmVirtualLines.RecordNo);
                if (salesId != localSalesLine.SalesId)
                {
                    treeControl.expand(treeEntry);
                    treeEntry           = treeControl.addItem(0,
                                                              0,
                                                              new FormTreeItem(strFmt('%1 %2',
                                                                                      "@SYS9694",
                                                                                      localSalesLine.SalesId),
                                                              imageListAppl_Sales.image(#ImageReqRefSales)));
                    salesId             = localSalesLine.SalesId;
                }
                itemName        = localSalesLine.itemName();
                itemId          = localSalesLine.ItemId;
                salesCategory   = localSalesLine.SalesCategory;
                name            = localSalesLine.Name;
                break;

            case tableNum(ProjInvoiceItem):
                localProjInvoiceItem = ProjInvoiceItem::findRecId(tmpFrmVirtualLines.RecordNo);
                if (projInvoiceId != localProjInvoiceItem.ProjInvoiceId || projInvoiceSalesId != localProjInvoiceItem.SalesId )
                {
                    treeControl.expand(treeEntry);
                    treeEntry           = treeControl.addItem(0,
                                                              0,
                                                              new FormTreeItem(strFmt('%1 %2 %3 %4',
                                                                                      "@SYS12128",
                                                                                      localProjInvoiceItem.ProjInvoiceId,
                                                                                      "@SYS9694",
                                                                                      localProjInvoiceItem.SalesId),
                                                              imageListAppl_Sales.image(#ImageInvoice)));
                    invoiceId           = localProjInvoiceItem.ProjInvoiceId;
                    invoiceSalesId      = localProjInvoiceItem.SalesId;
                }
                itemName        = localProjInvoiceItem.itemName();
                itemId          = localProjInvoiceItem.ItemId;
                // No sales category for proj
                salesCategory   = 0;
                name            = localProjInvoiceItem.Txt;
                break;
        }
        if (itemId != '')
        {
            treeControl.addItem(treeEntry,
                                0,
                                new FormTreeItem(strFmt('%1 %2 %3 %4',
                                                        "@SYS14578",
                                                        tmpFrmVirtualLines.Qty,
                                                        "@SYS7407",
                                                        itemName),
                                imageListAppl_Sales.image(#ImageItem)));
        }
        else
        {
            treeControl.addItem(treeEntry,
                                0,
                                new FormTreeItem(strFmt('%1 %2 %3 %4 %5',
                                                        "@SYS14578",
                                                        tmpFrmVirtualLines.Qty,
                                                        "@SYS50039",
                                                        EcoResCategory::find(salesCategory).Name,
                                                        name),
                                imageListAppl_Sales.image(#ImageItem)));
        }
    }
    treeControl.expand(treeEntry);
}

boolean canClose()
{
    System.NullReferenceException nullRefException;
    boolean         canClose;

    using (var activityContext = instrumentationLogger.salesCopyingActivities().canClose())
    {
        canClose = super();

        try
        {
            if (element.closedOk() && canClose)
            {
                canClose = this.validateCanClose();

                if (canClose)
                {
                    // Only update return reason code when it is from Customer service form
                    if (shouldOpenReturnTableForm && isMCRReturnOrder)
                    {
                        this.updateReturnOrder();
                    }

                    this.canClosePostProcess();
                }
            }
        }
        catch (nullRefException)
        {
            throw error("@SCM:PostingCreditNoteFailed");
            canClose = false;
        }
        catch
        {
            canClose = false;
        }
        return canClose;
    }
}

    /// <summary>
    /// Checks whether void fiscal document is allowed.
    /// </summary>
    /// <param name = "_transToBeVoided"><c>CustInvoiceJour</c> record.</param>
    /// <returns>True if void fiscal document is allowed and False vice versa.</returns>
	protected boolean checkAllowsVoidFiscalDocument_BR(common _transToBeVoided)
    {
        CustInvoiceJour localCustInvoiceJour;

        if (salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR
        && _transToBeVoided is CustInvoiceJour)
        {
            localCustInvoiceJour = _transToBeVoided;

            if (localCustInvoiceJour.SalesType == SalesType::ReturnItem)
            {
                return false;
            }
        }

        return true;
    }

