{
    kittingFormHandler = FeatureStateProvider::IsFeatureEnabled(KittingFeature::instance()) ? _formHandler : null;
}

public void closeOk()
{
    // Verify whether selected invoice is retail cash and carry order.
    if(RetailSalesCashAndCarryOrderWarningDialogFlight::instance().isEnabled())
    {
        SalesTable selectedSalesTable = SalesTable::find(custInvoiceJour.SalesId);

        if (calledFromReturnTable && selectedSalesTable.isRetailCashAndCarrySales() == NoYes::Yes)
        {
            DialogButton box = box::yesNo(strFmt("@Retail:RetailSalesCashAndCarryReturnOrderWarning"), DialogButton::No);

            if (box == DialogButton::No)
            {
                return;
            }
        }
    }

    if (TaxParameters::find().CashDiscOnInvoice && salesPurchCopy)
    {
        switch (salesPurchCopy)
        {
            case SalesPurchCopy::CreditNoteHeader:
            // <GBR>
            case SalesPurchCopy::VoidFiscalDocument_BR :
                // </GBR>
                this.creditNoteMessage();
                break;

            case SalesPurchCopy::CreditNoteLines:
                this.creditNoteMessage();
                break;

            case SalesPurchCopy::CopyAllHeader :
                if (reverseSign.value() == true)
                {
                    this.creditNoteMessage();
                }
                break;

            case SalesPurchCopy::CopyJournalHeader:
                if (reverseSign.value() == true)
                {
                    this.creditNoteMessage();
                }
                break;

            default:
                break;
        }
    }
    super();

    if (shouldOpenReturnTableForm && isMCRReturnOrder)
    {
        this.openReturnTableForm();
    }
}

/// <summary>
/// Updates the return reason code id of a return order.
/// </summary>
internal void updateReturnOrder()
{
    SalesTable updateReturnReasonSOTable;

    ttsbegin;

    select firstonly forupdate updateReturnReasonSOTable
            where updateReturnReasonSOTable.SalesId == callerSalesTable.SalesId;

    updateReturnReasonSOTable.ReturnReasonCodeId = EditReturnReasonCode.valueStr();

    if (updateReturnReasonSOTable.validateWrite())
    {
        updateReturnReasonSOTable.update();
    }

    ttscommit;
}

/// <summary>
/// Creates a <c>SalesOrderCopyingContract</c> object based on current UI values.
/// </summary>
/// <param name="_salesPurchCopy">
/// A value from the <c>SalesPurchCopy</c> enum to initialize the return contract with.
/// </param>
/// <returns>
/// An instance of the <c>SalesOrderCopyingContract</c> class.
/// </returns>
protected SalesOrderCopyingContract createContract(SalesPurchCopy _salesPurchCopy)
{
    SalesOrderCopyingContract contract = SalesOrderCopyingContract::newIsCreditNote(this.isCreditNote());
    //<GIN>
    SalesReturnType_IN salesReturnType = SalesReturnType_IN::None;
    //</GIN>

    contract.parmSalesPurchCopy(_salesPurchCopy);
    contract.parmCallingTableSalesId(callerSalesTable.SalesId);
    contract.parmTmpFrmVirtualLines(tmpFrmVirtualLines);
    contract.parmTmpFrmVirtualHeader(tmpFrmVirtualHeader);
    contract.parmQtyFactor(qtyFactor.realValue());
    contract.parmRecalculateAmount(recalculateAmount.value());
    contract.parmReverseSign(reverseSign.value());
    contract.parmCopyMarkup(copyMarkup.value());
    contract.parmCopyPrecisely(copyPrecisely.value());
    contract.parmDeleteLines(deleteLines.value());
    contract.parmReasonTableRef(reasonTableRef);

    if (countryRegion_RU || countryRegion_EU)
    {
        GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceEEU00011, funcName());

        contract.parmCreateCorrectionLine(createCorrectiveLines_PL.value());
        contract.parmCopyExchangeRate(copyExchRate_W.value());
        contract.parmReasonTableRef(reasonTableRef);
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        contract.parmReasonTableRef(reasonTableRef);
    }
    // </GBR>

    // <GIN>
    if (TaxParameters::checkTaxParameters_IN())
    {
        contract.parmTaxAsPerOrginalInvoice_IN(taxPerOriginalInvoice_IN.value());
        contract.parmSalesReturnType_IN(str2enum(salesReturnType, CustomerReturnType_IN.valueStr()));
    }
    // </GIN>

    // <GMY>
    if (countryRegion_MY)
    {
        contract.parmReasonTableRef(reasonTableRef);
    }
    // </GMY>

    contract.parmCustInvoiceJournal(custInvoiceJour);

    // <GTH>
    if (TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled())
    {
        contract.parmNoteFormat_TH(NoteFormat_TH.selection());
    }
    // </GTH>

    return contract;
}

void creditNoteMessage()
{
    CustTable           custTable;

    if (custInvoiceJour.InvoiceAmount > 0)
    {
        select firstonly CashDisc from custTable
            where custTable.AccountNum == custInvoiceJour.OrderAccount;

        if (custTable.CashDisc)
        {
            info("@SYS99050");
        }
    }
}

NoYes editCopy(boolean _set,
    Common             _line,
    Num                _id,
    LineNum            _lineNum,
    TransDate          _transDate,
    Qty                _qty,
    NoYes              _copy,
    PdsCWInventQty     _pdsCWQty = 0)
{
    // <GEERU>
    SalesCopying salesCopying;
    // </GEERU>

    element.findTmpFrmVirtual(tmpFrmVirtualLines, _line.TableId, _line.RecId);

    copy = tmpFrmVirtualLines ? NoYes::Yes : NoYes::No;

    if (_set)
    {
        if (this.mustCopyLine(_copy))
        {
            // <GEERU>
            if (countryRegion_RU)
            {
                select count(RecId) from tmpFrmVirtualHeader;
                if (tmpFrmVirtualHeader.RecId == 0)
                {
                    salesCopying = SalesCopying::construct(salesPurchCopy);
                    salesCopying.initParameters(callerSalesTable,
                                                null,
                                                null);
                }
            }
            if (countryRegion_RU &&
                tmpFrmVirtualHeader.RecId == 0 &&
                ! salesCopying.checkLineAgreement(_line))
            {
                copy   = NoYes::No;
            }
            else
            {
                // </GEERU>
                element.writeTmpFrmVirtual(tmpFrmVirtualLines,
                                       _line.TableId,
                                       _line.RecId,
                                       _id,
                                       _lineNum,
                                       _transDate,
                                       _qty
                                       , _pdsCWQty
                                       );

                if (_line.TableId == tablenum(CustInvoiceTrans))
                {
                    if (!this.isSalesHeaderChecked())
                    {
                        info("@Retail:ReturnLineSelectedWithoutHeader");
                    }
                }

                copy   = NoYes::Yes;
                // <GEERU>
            }
            // </GEERU>
        }
        else
        {
            tmpFrmVirtualLines.delete();
            copy   = NoYes::No;
        }
    }

    if (!markAllLines)
    {
        element.updateTreeControl();
    }

    return copy;
}

/// <summary>
/// Determines whether a line must be copied.
/// </summary>
/// <param name = "_copyChecked">true if copy line UI element is checked; otherwise, false.</param>
/// <returns>true if a selected line must be copied; otherwise, false.</returns>
protected boolean mustCopyLine(boolean _copyChecked)
{
    return _copyChecked;
}

NoYes editMarkAll(boolean           _set,
    Common            _header,
    Num               _id,
    NoYes             _markAll,
    FormDataSource    _lineDataSource)
{

    NoYes markAll;

    element.findTmpFrmVirtual(tmpFrmVirtualHeader, _header.TableId, _header.RecId);

    if (tmpFrmVirtualHeader)
    {
        markAll   = NoYes::Yes;
    }

    if (_set)
    {
        markAllLines = true;

        if (this.mustMarkAllLines(_markAll))
        {
            // <GBR>
            if (BrazilParameters::isEnabled()
                && !this.checkAllowsVoidFiscalDocument_BR(_header))
            {
                return NoYes::No;
            }
            // </GBR>

            // <GEERU>
            SalesCopying salesCopying;

            if (countryRegion_RU)
            {
                salesCopying = SalesCopying::construct(salesPurchCopy);
                salesCopying.initParameters(callerSalesTable,
                    null,
                    null);
            }
            if (countryRegion_RU && ! salesCopying.checkHeaderAgreement(_header))
            {
                markAll= NoYes::No;
            }
            else
            {
                // </GEERU>
                element.writeTmpFrmVirtual(tmpFrmVirtualHeader,
                    _header.TableId,
                    _header.RecId,
                    _id);

                markAll= NoYes::Yes;

                this.copyLinesData(true, _lineDataSource);
                // GEERU
            }
            // </GEERU>
        }
        else
        {
            tmpFrmVirtualHeader.delete();
            markAll= NoYes::No;

            this.copyLinesData(false, _lineDataSource);
        }

        markAllLines = false; // Reset to false so that we update tree control when only mark or unmark lines.
        element.updateTreeControl();
    }

    return markAll;
}

/// <summary>
/// Determines whether all lines must be marked.
/// </summary>
/// <param name = "_markAllChecked">true if mark all lines UI element is checked; otherwise, false.</param>
/// <returns>true if all lines must be marked; otherwise, false.</returns>
protected boolean mustMarkAllLines(boolean _markAllChecked)
{
    return _markAllChecked;
}

private void copyLinesData(boolean _doCopying, FormDataSource _lineDataSource)
{
    Object lineDataSource = _lineDataSource;
    QueryRun queryRun = new QueryRun(_lineDataSource.query());

    while (queryRun.next())
    {
        // <GEEU>
        if ((countryRegion_RU || countryRegion_EU) &&
            this.isPreviouslyCorrected_PL(queryRun.get(_lineDataSource.table())))
        {
            continue;
        }
        // </GEEU>

        lineDataSource.copy(true, queryRun.get(_lineDataSource.table()), _doCopying);
    }
}

Qty editQty(boolean             _set,
            Common              _line,
            Num                 _id,
            LineNum             _lineNum,
            TransDate           _transDate,
            Qty                 _origQty,
            Qty                 _qty,
            FormDataSource      _lineDataSource,
            UnitOfMeasureSymbol _unitID)
{
    Object  lineDataSource = _lineDataSource;
    Qty     qty;

    _qty = decRound( _qty, UnitOfMeasure::unitOfMeasureDecimalPrecision(
        UnitOfMeasure::unitOfMeasureIdBySymbol(_unitID)));

    element.findTmpFrmVirtual(tmpFrmVirtualLines, _line.TableId, _line.RecId);

    if (tmpFrmVirtualLines)
    {
        qty = tmpFrmVirtualLines.Qty;
    }
    else
    {
        qty = _origQty;
        // <GBR>
        if (BrazilParameters::isEnabled())
        {
            qty = _origQty - _qty;
        }
        // </GBR>
    }

    if (_set)
    {
        if (!tmpFrmVirtualLines)
        {
            lineDataSource.copy(true, _line, true);
        }

        qty = _qty;

        element.findTmpFrmVirtual(tmpFrmVirtualLines, _line.TableId, _line.RecId);

        element.writeTmpFrmVirtual(tmpFrmVirtualLines,
                                   _line.TableId,
                                   _line.RecId,
                                   _id,
                                   _lineNum,
                                   _transDate,
                                   qty);
    }

    return qty;
}

public edit ReasonCode editReasonCode(boolean _set, ReasonCode _reasonCode)
{
    ReasonCode      tmpReasonCode = strLRTrim(_reasonCode);

    if (_set)
    {
        if (reasonTableRef == 0)
        {
            reasonTableRef = ReasonTableRef::createFromCode(tmpReasonCode);
        }
        else
        {
            reasonTableRef = ReasonTableRef::updateCode(tmpReasonCode, reasonTableRef);
        }

        // Update the reason comment with the default comment value for the updated reason code.
        // A blank reasonCode will result in a blank comment because a blank reasonCode
        // will return an empty reasonTable buffer.

        this.editReasonComment(true, ReasonTable::find(_reasonCode).Description);
    }
    else
    {
        tmpReasonCode = ReasonTableRef::find(reasonTableRef).Reason;
    }

    return tmpReasonCode;
}

public edit ReasonComment editReasonComment(boolean _set, ReasonComment _reasonComment)
{
    ReasonComment   tmpReasonComment = _reasonComment;

    if (_set)
    {
        // If the ReasonRefRecID is 0, then create a new ReasonTableRef record
        // otherwise, update the existing record.
        if (reasonTableRef == 0)
        {
            reasonTableRef = ReasonTableRef::createFromComment(tmpReasonComment);
        }
        else
        {
            reasonTableRef = ReasonTableRef::updateComment(tmpReasonComment, reasonTableRef);
        }
    }
    else
    {
        tmpReasonComment = ReasonTableRef::find(reasonTableRef).ReasonComment;
    }

    return tmpReasonComment;
}

void filterDateModified(date _fromDate, date _todate)
{
    #define.blank('')

    if (!_fromdate && !_todate)
    {
        rangeInvoiceDate.value(#blank);
    }
    else
    {
        rangeInvoiceDate.value(strFmt("%1..%2", _fromDate, _toDate));
    }

    custInvoiceJour_ds.executeQuery();
}

void findTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtual, TableId _tableId, RecId _recId)
{
    select _tmpFrmVirtual
        where _tmpFrmVirtual.TableNum   == _tableId &&
              _tmpFrmVirtual.RecordNo   == _recId;
}

