public void initParmDefault()
{
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL, #isoHU]) && CreateCorrectiveLines_PL.visible())
    {
        createCorrectiveLines = NoYes::Yes;
    }
}

public container pack()
{
    return [CurrentVersion, createCorrectiveLines];
}

public boolean unpack(container packedClass)
{
    Integer version = conPeek(packedClass, 1);
    switch (version)
    {
        case CurrentVersion:
            [version, createCorrectiveLines] = packedClass;
            break;
        default:
            return false;
    }
    return true;
}

public void close()
{
    createCorrectiveLines = createCorrectiveLines_PL.value();
    xSysLastValue::saveLast(this);
    super();
}

public void run()
{
    super();
    xSysLastValue::getLast(this);
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL, #isoHU]) && CreateCorrectiveLines_PL.visible())
    {
        CreateCorrectiveLines_PL.value(createCorrectiveLines);
    }
}

void  init()
{
    boolean isInterCompanyPOInvoiceMatched;

    instrumentationLogger = SalesInstrumentationLogger::createLogger(formStr(SalesCopying));

    #winApi

    // <GEERU><GIN><GEEU>
    countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    countryRegion_IN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    countryRegion_EU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT, #isoLV, #isoEE, #isoCZ, #isoPL, #isoHU]);
    countryRegion_RC = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoDE, #isoGB, #isoIE, #isoNL, #isoFR, #isoIS, #isoDK]);
    // </GIN></GEERU></GEEU>
    // <GMY>
    countryRegion_MY = TaxGSTFeatureChecker_MY::isCountryRegionMY();
    // </GMY>

    // <GEEPL>
    countryRegion_PL = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]);
    // <GEEPL>

    countryRegion_TH = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoTH]);

    if (!element.args().record() || !element.args().parmEnumType())
    {
        throw error("@SYS22539");
    }

    // If called from customer service form's return order menu
    if (element.args().menuItemName() == menuitemDisplayStr(MCRReturnFindSalesOrder))
    {
        selectedSalesId = element.args().parm();
        calledFromCustomerService = true;

        // Initialize a new return order based on customer selected on return order screen
        element.initReturnOrderFromCustomer();
        element.generateQueryRangeForMergedCustomers(element.args().record());
    }
    else
    {
        callerSalesTable  = element.args().record();
        allowCallerUpdate = hasTableAccess(element.args().record().TableId, AccessType::Add) && callerSalesTable.checkUpdate(false);
    }

    salesPurchCopy      = element.args().parmEnum();
    // <GEEPL>
    if (countryRegion_PL && (!calledFromCustomerService))
    {
        callerDataSource = element.args().record().dataSource();
    }
    // </GEEPL>

    super();

    isMCRReturnOrder =  callerSalesTable.returnItem() && calledFromCustomerService;

    if (isMCRReturnOrder)
    {
        ReturnReasonCode.visible(true);

        if (!editReturnReasonCode.valueStr() && CustParameters::find().ReturnOrdersReasonReq == NoYes::Yes)
        {
            EditReturnReasonCode.mandatory(true);
        }
    }

    // <GEEPL>
    if (callerDataSource)
    {
        callerDataSourceAllowEdit = callerDataSource.allowEdit();
    }
    // </GEEPL>

    imageListAppl_Sales = new ImageListAppl_Sales();

    treeControl.setImagelist(imageListAppl_Sales.imageList());

    qtyFactor.realValue(1);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    // <GEERU>
    if (countryRegion_RU)
    {
        copyExchRate_W.visible(false);
    }
    // </GEERU>

    switch (salesPurchCopy)
    {
        case SalesPurchCopy::CopyAllHeader:
            this.setDeleteLinesButton(allowCallerUpdate);
            copyHeader.value(NoYes::Yes);

        case SalesPurchCopy::CopyAllLines:
            element.design().caption("@SYS53043");
            // <GEEU>
            createCorrectiveLines_PL.visible(false);
            // </GEEU>
            break;

        case SalesPurchCopy::CopyJournalHeader:
            deleteLines.value(NoYes::Yes);
            copyHeader.value(NoYes::Yes);

        case SalesPurchCopy::CopyJournalLines:
            element.design().caption("@SYS53044");
            copyMarkup.value(NoYes::Yes);
            copyPrecisely.value(NoYes::Yes);
            tabPageSales.visible(false);
            // <GEEU>
            createCorrectiveLines_PL.visible(false);
            // </GEEU>
            break;

        case SalesPurchCopy::CreditNoteHeader:
        // <GBR>
        case SalesPurchCopy::VoidFiscalDocument_BR :
            // </GBR>
            this.setDeleteLinesButton(allowCallerUpdate);
            copyHeader.value(NoYes::Yes);
            // <GEERU>
            if (countryRegion_RU)
            {
                if (callerSalesTable.returnItem())
                {
                    copyExchRate_W.visible(false);
                }
                else
                {
                    copyExchRate_W.visible(true);
                    copyExchRate_W.value(copyExchRate_W.isVisible()); // take configuration key into account
                }
            }
        // </GEERU>

        case SalesPurchCopy::CreditNoteLines:
            element.design().caption("@SYS53045");
            reverseSign.value(NoYes::Yes);
            copyMarkup.value(NoYes::Yes);
            copyPrecisely.value(NoYes::Yes);

            reverseSign.allowEdit(false);
            reverseSign.skip(true);

            if (this.isCopyingCreditNoteLineFromReturnOrderLine())
            {
                element.design().caption("@SYS104351");
                copyMarkup.value(NoYes::No);

                searchCriteria.visible(true);
                qtyFactor.visible(false);
                recalculateAmount.visible(false);
                copyMarkup.visible(true);
                copyPrecisely.visible(false);
                copyHeader.visible(false);
                tabPageProjInvoice.visible(false);
                // Quantity to returned
                custInvoiceTrans_qty.label("@SYS104947");
                // <GEEU>
                createCorrectiveLines_PL.visible(false);
                // </GEEU>
                calledFromReturnTable = true;
            }
            else
            {
                qtyFactor.allowEdit(false);
                qtyFactor.skip(true);
                recalculateAmount.allowEdit(false);
                recalculateAmount.skip(true);
                copyMarkup.allowEdit(false);
                copyMarkup.skip(true);
                copyPrecisely.allowEdit(false);
                copyPrecisely.skip(true);
                copyHeader.allowEdit(false);
                copyHeader.skip(true);
            }
            tabPageSales.visible(false);
            tabPagePackingSlip.visible(false);
            tabPageQuotation.visible(false);
            tabPageConfirm.visible(false);
            TabPageInvoice.expand();
            // <GBR>
            if (BrazilParameters::isEnabled() &&
                salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR)
            {
                element.design().caption("@GLS63948");
            }
            // </GBR>
            // <GEERU>
            if (countryRegion_RU)
            {
                tabPageInvoice4Paym_RU.visible(false);
            }
            // </GEERU>
            // <GEEU>
            if (countryRegion_EU)
            {
                createCorrectiveLines_PL.visible(true);
                reasonCode.visible(true);

                if (CustParameters::find().CreditNoteReasonReq)
                {
                    editReasonComment.mandatory(true);
                }
                else
                {
                    editReasonComment.mandatory(false);
                }
            }
            // </GEEU>
            break;
    }

    isInterCompanyPOInvoiceMatched = callerSalesTable.isInterCompanyPOInvoiceMatched();

    if (callerSalesTable.MatchingAgreement || isInterCompanyPOInvoiceMatched)
    {
        copyHeader.value(NoYes::No);
        copyHeader.allowEdit(false);
        copyHeader.skip(true);
    }

    // <GIN>
    if (countryRegion_IN)
    {
        callingform =  element.args().caller().name();
        element.setVisible_IN();
    }
    // </GIN>
    if (isInterCompanyPOInvoiceMatched)
    {
        copyMarkup.value(NoYes::No);
        copyMarkup.allowEdit(false);
        copyMarkup.skip(true);
    }
}

void initReturnOrderFromCustomer()
{
    callerCustTable = element.args().record();

    callerSalesTable.clear();

    this.initSalesTable();

    if (callerSalesTable.validateWrite())
    {
        ttsbegin;
        callerSalesTable.insert();
        allowCallerUpdate = hasTableAccess(tableNum(SalesTable), AccessType::Add) && callerSalesTable.checkUpdate(false);
        ttscommit;
    }
    else
    {
        throw error("@Retail:ErrorInitializingReturnOrder");
    }
}

/// <summary>
/// Initializes sales table record fields.
/// </summary>
protected void initSalesTable()
{
    callerSalesTable.initValue(SalesType::ReturnItem);

    callerSalesTable.CustAccount        = callerCustTable.AccountNum;
    callerSalesTable.CustGroup          = callerCustTable.CustGroup;
    callerSalesTable.InvoiceAccount     = callerCustTable.AccountNum;

    callerSalesTable.SalesId            = NumberSeq::newGetNum(SalesParameters::numRefSalesId()).num();

    if (SalesCopyingSetReturnAddressFromInventLocationFlight::instance().isEnabled())
    {
        callerSalesTable.initFromCustTable();
        callerSalesTable.setReturnAddressFromInventLocationId(callerSalesTable.InventLocationId);
    }
    else
    {
        callerSalesTable.setReturnAddressFromInventLocationId(callerSalesTable.InventLocationId);
        callerSalesTable.initFromCustTable();
    }

    callerSalesTable.DeliveryDateControlType = SalesDeliveryDateControlType::None;
    SalesCalcAvailableDlvDates::initDateFields(callerSalesTable);
    callerSalesTable.ReturnDeadline = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()) + SalesParameters::find().ReturnPeriodOfValidity;

    callerSalesTable.ReturnItemNum      = NumberSeq::newGetNum(SalesParameters::numRefReturnItemNum(), true).num();

    callerSalesTable.ReturnStatus       = ReturnStatusHeader::Created;

    // Intialize the RMA defaults from the call ceneter order parameters.
    if (MCROrderParameters::isCallCenterEnabledAndInUse() && RetailMCRChannelTable::findForCurrentUser() &&
        callerSalesTable.SalesType == SalesType::ReturnItem)
    {
        McrOrderParameters mcrOrderParameters = McrOrderParameters::find();
        McrReturnSalesTable mcrReturnSalesTable = callerSalesTable.mcrReturnSalesTable();

        mcrReturnSalesTable.ApplyCreditToExchange = mcrOrderParameters.ApplyCreditToExchange;
        mcrReturnSalesTable.AdvanceExchange = mcrOrderParameters.AdvanceExchange;
        mcrReturnSalesTable.AdvanceCredit = mcrOrderParameters.AdvanceCredit;
        callerSalesTable.mcrPackMCRReturnSalesTable(mcrReturnSalesTable);
    }
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

/// <summary>
/// Determines if form is in credit note scenario.
/// </summary>
/// <returns>
/// Returns true if it's credit note scenario, false otherwise.
/// </returns>
private boolean isCreditNote()
{
    return salesPurchCopy == SalesPurchCopy::CreditNoteHeader || salesPurchCopy == SalesPurchCopy::CreditNoteLines
    // <GBR>
    ||(BrazilParameters::isEnabled() && salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR) ;
    // <\GBR>
}

public boolean isPreviouslyCorrected_PL(Common _common)
{
    CustInvoiceTrans localCustInvoiceTrans;
    CustInvoiceTrans tmpCustInvoiceTrans;
    CustInvoiceTrans_W tmpCustInvoiceTrans_W;
    SalesLine tmpSalesLine;
    TableId custInvoiceTransTableId = tableNum(CustInvoiceTrans);
    TableId projInvoiceItemTableId = tableNum(ProjInvoiceItem);

    if (ProjCreditNoteInitiatedViaSalesOrderCreditNoteFlight::instance().isEnabled())
    {
        if (_common.TableId == custInvoiceTransTableId)
        {
            return this.isPreviouslyCorrected_PL_CustInvoiceTrans(_common);
        }
        else if (_common.TableId == projInvoiceItemTableId)
        {
            return this.isPreviouslyCorrected_PL_ProjInvoiceItem(_common);
        }
        else
        {
            return false;
        }
    }
    else
    {
        if (_common.TableId != custInvoiceTransTableId)
        {
            return false;
        }

        localCustInvoiceTrans = _common;

        if (localCustInvoiceTrans.Qty < 0)
        {
            return true;
        }

        select firstonly tmpSalesLine
            where tmpSalesLine.InventTransIdReturn == localCustInvoiceTrans.InventTransId &&
                tmpSalesLine.RefReturnInvoiceTrans_W == localCustInvoiceTrans.RecId;

        if (tmpSalesLine)
        {
            return true;
        }

        select firstonly tmpCustInvoiceTrans
            exists join tmpCustInvoiceTrans_W
                where  tmpCustInvoiceTrans_W.CustInvoiceTrans == tmpCustInvoiceTrans.RecId
                    && tmpCustInvoiceTrans_W.RefReturnInvoiceTrans_W == localCustInvoiceTrans.RecId;

        if (tmpCustInvoiceTrans)
        {
            return true;
        }

        return false;
    }
}

private boolean isPreviouslyCorrected_PL_CustInvoiceTrans(Common _common)
{
    CustInvoiceTrans localCustInvoiceTrans, tmpCustInvoiceTrans;
    CustInvoiceTrans_W tmpCustInvoiceTrans_W;

    localCustInvoiceTrans = _common;

    if (this.isPreviouslyCorrected(localCustInvoiceTrans.Qty, localCustInvoiceTrans.InventTransId, localCustInvoiceTrans.RecId))
    {
        return true;
    }

    select firstonly tmpCustInvoiceTrans
        exists join tmpCustInvoiceTrans_W
        where  tmpCustInvoiceTrans_W.CustInvoiceTrans == tmpCustInvoiceTrans.RecId
            && tmpCustInvoiceTrans_W.RefReturnInvoiceTrans_W == localCustInvoiceTrans.RecId;

    if (tmpCustInvoiceTrans)
    {
        return true;
    }

    return false;
}

private boolean isPreviouslyCorrected_PL_ProjInvoiceItem(Common _common)
{
    ProjInvoiceItem localProjInvoiceItem;
    ProjInvoiceItem projInvoiceItemFromCreditNote;

    localProjInvoiceItem = _common;

    if (this.isPreviouslyCorrected(localProjInvoiceItem.Qty, localProjInvoiceItem.InventTransId, localProjInvoiceItem.RecId))
    {
        return true;
    }

    return false;
}

private boolean isPreviouslyCorrected(Qty _qty, TradeInventTransId _inventTransId, RecId _recid)
{
    SalesLine tmpSalesLine;

    if (_qty < 0)
    {
        return true;
    }

    select firstonly tmpSalesLine
        where tmpSalesLine.InventTransIdReturn == _inventTransId &&
            tmpSalesLine.RefReturnInvoiceTrans_W == _recid;

    if (tmpSalesLine)
    {
        return true;
    }

    return false;
}

/// <summary>
/// Returns the current catch weight qty
/// </summary>
/// <param name="_set">Set value to field</param>
/// <param name="_line">Sales line record</param>
/// <param name="_id">Record Id</param>
/// <param name="_lineNum">SalesLine Line number</param>
/// <param name="_transDate">SalesLine date</param>
/// <param name="_pdsCWOrigQty">Original catch weight qty</param>
/// <param name="_pdsCWQty">Updated catch weight  qty</param>
/// <param name="_lineDataSource">Sales Line record</param>
/// <param name="_itemId">current Item</param>
/// <param name="_unitID">Inventory unit</param>
/// <returns>returns field value or tmp talbe field value</returns>
public PdsCWInventQty pdsCWEditQty(
    boolean             _set,
    Common              _line,
    Num                 _id,
    LineNum             _lineNum,
    TransDate           _transDate,
    PdsCWInventQty      _pdsCWOrigQty,
    PdsCWInventQty      _pdsCWQty,
    FormDataSource      _lineDataSource,
    ItemId              _itemId,
    UnitOfMeasureSymbol _unitID)
{
    Object  lineDataSource = _lineDataSource;
    PdsCWInventQty     pdsCWInventQty;
    InventQty          inventQty;
    SalesLine          localSalesLine;

    element.findTmpFrmVirtual(tmpFrmVirtualLines, _line.TableId, _line.RecId);

    if (tmpFrmVirtualLines)
    {
        pdsCWInventQty = tmpFrmVirtualLines.PdsCWQty;
    }
    else
    {
        pdsCWInventQty = _pdsCWOrigQty;
    }

    if (_set)
    {
        if (!tmpFrmVirtualLines)
        {
            lineDataSource.copy(true, _line, true);
        }

        pdsCWInventQty = _pdsCWQty;

        if (calledFromReturnTable)
        {
            localSalesLine = SalesLine::findInventTransId(custInvoiceTrans.InventTransId);
        }
        if (localSalesLine)
        {
            inventQty = SalesLine::getInventQtyFromCWUnit(localSalesLine, pdsCWInventQty, localSalesLine.inventDim(),false);
        }
        else
        {
            inventQty = PdsCatchWeight::inventQty(_itemId, pdsCWInventQty, _unitID);
        }
        element.writeTmpFrmVirtual(tmpFrmVirtualLines,
                                   _line.TableId,
                                   _line.RecId,
                                   _id,
                                   _lineNum,
                                   _transDate,
                                   inventQty,
                                   pdsCWInventQty);
    }

    return pdsCWInventQty;
}

/// <summary>
/// Method sets the edit status of the catch weight fields
/// </summary>
/// <param name="_dataSource">Current record source</param>
public void pdsCWEnableFields(Common _dataSource)
{
    boolean cwItem = false;

    switch (_dataSource.TableId)
    {
        case tableNum(SalesLine):
            cwItem = PdsGlobal::pdsIsCWItem(salesLine.ItemId);
            salesLine_qty.allowEdit(!cwItem);
            salesLine_PdsCWQty.allowEdit(cwItem);
            break;
        case tableNum(CustConfirmTrans):
            cwItem = PdsGlobal::pdsIsCWItem(custConfirmTrans.ItemId);
            custConfirmTrans_qty.allowEdit(!cwItem);
            custConfirmTrans_PdsCWQty.allowEdit(cwItem);
            break;
        case tableNum(CustPackingSlipTrans):
            cwItem = PdsGlobal::pdsIsCWItem(custPackingSlipTrans.ItemId);
            custPackingSlipTrans_qty.allowEdit(!cwItem);
            custPackingSlipTrans_PdsCWQty.allowEdit(cwItem);
            break;
        case tableNum(CustInvoiceTrans):
            cwItem = PdsGlobal::pdsIsCWItem(custInvoiceTrans.ItemId);
            custInvoiceTrans_qty.allowEdit(!cwItem);
            custInvoiceTrans_pdsCWQty.allowEdit(cwItem);
            break;
        // <GEERU>
        case (tableNum(CustInvoice4PaymTrans_RU)):
            cwItem = PdsGlobal::pdsIsCWItem(custInvoice4PaymTrans_RU.ItemId);
            invoice4PaymTrans_qty.allowEdit(!cwItem);
            invoice4PaymTrans_pdsCWQty.allowEdit(cwItem);
            break;
        // </GEERU>
    }
}

/// <summary>
///     Calculates the total invoiced quantity for a sales order line.
/// </summary>
/// <param name="_custInvoiceTrans">
///     A buffer of the <c>CustInvoiceTrans</c> table.
/// </param>
/// <returns>
///     A SalesQty type.
/// </returns>
public PdsCWSalesQty pdsCWUpdateQtyTotalInvoiced(CustInvoiceTrans _custInvoiceTrans)
{
    this.updateQtyTotalInvoiced(_custInvoiceTrans);
    return pdsCWQtyTotalInvoicedCacheValue;
}

/// <summary>
/// Prompts user about currency conversion and saves users decision in the copying contract.
/// </summary>
private void promptConvertCurrency()
{
    boolean convertCurrency;
    TradeCurencyConversion tradeCurencyConversion;

    [convertCurrency, tradeCurencyConversion] = SalesCopying::promptConvertCurrencyCodeOnServer(salesOrderCopyingContract.pack(), this.isCreditNote());

    salesOrderCopyingContract.parmConvertCurrency(convertCurrency);
    salesOrderCopyingContract.parmTradeCurencyConversion(tradeCurencyConversion);
}

/// <summary>
/// Prompts user for input on deleting existing settlements in Credit Note scenario
/// </summary>
private void promptRemoveSettlement()
{
    boolean deleteSettlement;
    SalesCreditNoteCopyingContract contract = salesOrderCopyingContract as SalesCreditNoteCopyingContract;

    if (contract != null)
    {
        deleteSettlement = SalesCopying_CreditNote::promptRemoveSettlementOnServer(contract.pack());
        contract.parmDeleteSettlement(deleteSettlement);
    }
}

private void setVisible_IN()
{
    taxPerOriginalInvoice_IN.visible(TaxParameters::checkTaxParameters_IN() &&
    (salesPurchCopy == SalesPurchCopy::CreditNoteHeader)                    ||
    (salesPurchCopy == SalesPurchCopy::CreditNoteLines));

    CustomerReturnType_IN.visible(TaxParameters::checkTaxParameters_IN() &&
    (callingform == formStr(SalesTable)                                  ||
    callingform  == formStr(SalesTableListPage)                          ||
    callingform  == formStr(ReturnTableListPage)                         ||
    callingform  == formStr(ReturnTable)));
}

/// <summary>
/// Retrieves the returned quantities for a sales order line.
/// </summary>
/// <param name = "_custInvoiceTrans">
/// A <c>CustInvoiceTrans</c> record used to find the sales order line.
/// </param>
/// <param name = "_salesLine">
/// A <c>SalesLine</c> record used to determine the project id.
/// </param>
/// <returns>
/// A container that contains the <c>SalesOrderedQty</c> and <c>PdsCWQty</c> type specifying the sum of
/// returned items.
/// </returns>
protected container retrieveRemainReturnLotQtyAllCreditNote(CustInvoiceTrans _custInvoiceTrans, SalesLine _salesLine)
{
    return SalesLine::pdsRemainReturnLotQtyAllCreditNote(_custInvoiceTrans.InventTransId, _salesLine.ProjId, _custInvoiceTrans.SalesUnit);
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(salesLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

/// <summary>
///     Calculates the total invoiced quantity for a sales order line.
/// </summary>
/// <param name="_custInvoiceTrans">
///     A buffer of the <c>CustInvoiceTrans</c> table.
/// </param>
/// <returns>
///     A SalesQty type.
/// </returns>
public SalesQty updateQtyTotalInvoiced(CustInvoiceTrans _custInvoiceTrans)
{
    //Even if the original SalesLine has been deleted the following retrieves the total invoiced quantity.
    if (salesLineBuffer.InventTransId != _custInvoiceTrans.InventTransId)
    {
        salesLineBuffer.InventTransId = _custInvoiceTrans.InventTransId;
        qtyTotalInvoicedCacheValue    = salesLineBuffer.invoicedInTotal();
        salesLineBuffer.ItemId        = _custInvoiceTrans.ItemId;
        pdsCWQtyTotalInvoicedCacheValue = salesLineBuffer.pdsCWInvoicedInTotal();
    }

    return qtyTotalInvoicedCacheValue;
}

void writeTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtual, TableId _tableId, RecId _recId, Num _id, LineNum _lineNum = 0, TransDate _transDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), Qty _qty = 0
                        , PdsCWInventQty _pdsCWQty = 0
                       )
{
    _tmpFrmVirtual.TableNum     = _tableId;
    _tmpFrmVirtual.RecordNo     = _recId;
    _tmpFrmVirtual.Id           = _id;
    _tmpFrmVirtual.LineNum      = _lineNum;
    _tmpFrmVirtual.TransDate    = _transDate;
    _tmpFrmVirtual.Qty          = _qty;

    if (#PdsCatchWeightEnabled)
    {
        _tmpFrmVirtual.PdsCWQty = _pdsCWQty;
    }

    _tmpFrmVirtual.write();
}

/// <summary>
/// Determines if the copy is from a return order line to a credit note line.
/// </summary>
/// <returns>
/// true if the sales order where lines are about to be copied to (the callerSalesTable) is of the type SalesType::ReturnItem; otherwise, false.
/// </returns>
protected boolean isCopyingCreditNoteLineFromReturnOrderLine()
{
    return callerSalesTable.returnItem();
}

    /// <summary>
    /// Determines if form can closed.
    /// </summary>
    /// <returns>
    /// true if the form can closed; otherwise, false.
    /// </returns>
    protected boolean validateCanClose()
    {
        boolean         canClose = true;

        // <GEEU>
        InvoiceDate     tmpInvoiceDate;
        RecId           tmpRecId;
        CustInvoiceJour tmpCustInvoiceJour;
        // </GEEU>

        // <GBR>
        FiscalDocument_BR fiscalDocument;
        // </GBR>

        salesOrderCopyingContract = this.createContract(salesPurchCopy);

        // <GEEU>
        if ((countryRegion_RU || countryRegion_EU || countryRegion_TH || countryRegion_RC || countryRegion_MY)
                && salesPurchCopy == SalesPurchCopy::CreditNoteHeader
                && CustParameters::find().CreditNoteReasonReq
                && !reasonTableRef)
        {
            throw error("@AccountsReceivable:CreditNoteReasonEmptyError");
        }
        // </GEEU>

        canClose = callerSalesTable.checkUpdate(true);

        if (allowCallerUpdate && canClose)
        {
            if (copyHeader.value())
            {
                // <GEEU>
                if (countryRegion_EU || countryRegion_RU)
                {
                    while select tmpFrmVirtualHeader
                    {
                        tmpCustInvoiceJour = CustInvoiceJour::findRecId(tmpFrmVirtualHeader.RecordNo);
                        if (tmpCustInvoiceJour.InvoiceDate > tmpInvoiceDate)
                        {
                            tmpInvoiceDate = tmpCustInvoiceJour.InvoiceDate;
                            tmpRecId = tmpCustInvoiceJour.RecId;
                        }
                    }

                    if (tmpRecId)
                    {
                        delete_from tmpFrmVirtualHeader
                                where tmpFrmVirtualHeader.RecordNo != tmpRecId;
                    }
                }
                // </GEEU>

                select count(RecId) from tmpFrmVirtualHeader;
                if (tmpFrmVirtualHeader.RecId > 1)
                {
                    canClose = checkFailed("@SYS29009");
                }
            }
            else
            {
                // <GBR>
                if (!BrazilParameters::isEnabled()
                        && salesPurchCopy != SalesPurchCopy::VoidFiscalDocument_BR)
                {
                    // </GBR>
                    delete_from tmpFrmVirtualHeader;
                    // <GBR>
                }
                // </GBR>
            }

            // <GBR>
            if (canClose
                && BrazilParameters::isEnabled()
                && salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR)
            {
                select tmpFrmVirtualHeader;
                fiscalDocument = FiscalDocument_BR::findByRef(tmpFrmVirtualHeader.TableNum, tmpFrmVirtualHeader.RecordNo);

                canClose = canClose && EFDocUserInterfaceUtil_BR::constructFromFiscalDocument(fiscalDocument).checkReversal();

                canClose = canclose && SalesTable_BR::ThereIsNoOtherOrderLinkedToFiscDoc(fiscalDocument.RefRecId);
            }
            // </GBR>

            // <GEERU>
            if (countryRegion_RU)
            {
                canClose = canClose && SalesCopying::checkLinesAgreementServer(salesOrderCopyingContract.pack(), this.isCreditNote());
            }
            // </GEERU>
        }

        if (isMCRReturnOrder && canClose)
        {
            if(editReturnReasonCode.valueStr())
            {
                shouldOpenReturnTableForm = true;
            }
            else
            {
                if (CustParameters::find().ReturnOrdersReasonReq == NoYes::Yes)
                {
					boolean isBrazilCancelFiscalDocument = BrazilParameters::isEnabled() && salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR;
                    shouldOpenReturnTableForm = false;
                    canClose = isBrazilCancelFiscalDocument || checkFailed(strFmt("@SYS116347", fieldPName(SalesTable, ReturnReasonCodeId)));
                }
                else
                {
                    shouldOpenReturnTableForm = true;
                }
            }
        }

        return canClose;
    }

/// <summary>
/// Execute the last business process of canClose;
/// </summary>
protected void canClosePostProcess()
{
    Object          salesTable_dataSource;

    this.promptRemoveSettlement();
    this.promptConvertCurrency();
    SalesCopying::copyServer(salesOrderCopyingContract.pack(), this.isCreditNote());

    if (!calledFromCustomerService)
    {
        salesTable_dataSource = FormDataUtil::getFormDataSource(callerSalesTable);
        if (salesTable_dataSource)
        {
            if (formDataSourceHasMethod(salesTable_dataSource, identifierStr(editSales)))
            {
                salesTable_dataSource.editSales();
            }

            salesTable_dataSource.reRead();
            salesTable_dataSource.reFresh();
            salesTable_dataSource.reReadReferenceDataSources();
            if (formDataSourceHasMethod(salesTable_dataSource, identifierStr(reReadLines)))
            {
                salesTable_dataSource.reReadLines();
            }
        }
    }
}

/// <summary>
/// Determines whether return item quantity validation is required.
/// </summary>
/// <returns>
/// true if return item quantity validation is required; otherwise, false.
/// </returns>
protected boolean mustValidateReturnItemQty()
{
    return (callerSalesTable.returnItem() || salesPurchCopy == SalesPurchCopy::CreditNoteHeader || salesPurchCopy == SalesPurchCopy::CreditNoteLines);
}

/// <summary>
/// Determines if search criteria is visible.
/// </summary>
/// <returns>
/// true if search criteria is visible; otherwise, false.
/// </returns>
protected boolean isSearchCriteriaVisible()
{
    return callerSalesTable.returnItem();
}

/// <summary>
/// Deletes the created return order on cancel click.
/// </summary>
public void closeCancel()
{
    SalesTable cancelReturnTable;
    McrReturnSalesTable mcrReturnSalesTable;

    super();

    // When this is called from customer service it inserts the return order right away, which we need to delete now.
    if (calledFromCustomerService)
    {
        RetailCallCenterEventLog::EventWriteSalesCopyingClosedDeleteReturnOrder(funcName(), callerSalesTable.RecId);

        delete_from cancelReturnTable
            where cancelReturnTable.RecId == callerSalesTable.RecId;

        delete_from mcrReturnSalesTable
            where mcrReturnSalesTable.SalesTable == callerSalesTable.RecId;
    }
}

/// <summary>
/// Opens the return table form for the current caller sales table record.
/// </summary>
private void openReturnTableForm()
{
    Args args;

    args = new Args();
    args.record(callerSalesTable);
    new MenuFunction(MenuItemDisplayStr(MCRCreateReturnOrder), MenuItemType::Display).run(Args);
}

/// <summary>
/// Creates a query string which will load the merged customers
/// </summary>
/// <returns>
/// The created query string with the loaded merged customers
/// </returns>
/// <remarks>
/// Example of string contents:
/// ((InvoiceAccount == "2001") || (InvoiceAccount == "2002"))
/// </remarks>
private str generateQueryRangeForMergedCustomers(CustTable _custTable)
{
    CustTable custTableLocal;

    // Add the primary and merged customers to the query string.
    customerQueryStr += strfmt('(%1 == "%2")', fieldStr(CustInvoiceJour, OrderAccount), _custTable.AccountNum);

    while select AccountNum from custTableLocal
            where custTableLocal.mcrMergedRoot == _custTable.AccountNum
    {
        isMergedCustomerAccount = true;
        customerQueryStr += ' || ' + strfmt('(%1 == "%2")', fieldStr(CustInvoiceJour, OrderAccount), custTableLocal.AccountNum);
    }

    return customerQueryStr;

}

internal boolean isSalesHeaderChecked()
{
    SalesTable orderHeader;
    CustInvoiceTrans invoiceLine;
    boolean isRetailOrder = false;

    select orderHeader
        join invoiceLine where invoiceLine.SalesId == orderHeader.SalesId
            && invoiceLine.InvoiceId == tmpFrmVirtualLines.Id;

    // To check whether Retail order or not
    if (orderHeader.RecId != 0 && orderHeader.isRetailSale())
    {
        isRetailOrder = true;
    }

    // To check return line is selected without header selection
    return !(!tmpFrmVirtualHeader && tmpFrmVirtualLines && isRetailOrder);
}

