edit SalesOrderedQty qty(boolean    _set,
             SalesLine  _salesLine,
             Qty        _qty)
{
    Qty         qty = _salesLine.SalesQty;
    Factor      salesQtyFactor = 1.0;

    if (_salesLine.SalesQty)
        salesQtyFactor = _qty / _salesLine.SalesQty;

    qty = element.editQty(_set,
                       _salesLine,
                       _salesLine.SalesId,
                       _salesLine.LineNum,
                       DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                       _salesLine.SalesQty,
                       _qty,
                       salesLine_ds,
                       _salesLine.SalesUnit);

    return qty;
}

