boolean validateCreditNoteExist()
{
    SalesLine salesLineLoc;
    SalesTable salesTableLoc;
    boolean ret = true;

    select firstonly LineNum, SalesId, RecId from salesLineLoc
        where salesLineLoc.RefReturnInvoiceTrans_W == curCustInvoiceTransRecId
    exists join salesTableLoc
        where salesTableLoc.SalesId == salesLineLoc.SalesId
            && salesTableLoc.ReturnStatus != ReturnStatusHeader::Canceled;

    if (salesLineLoc.RecId != 0)
    {
        ret = checkFailed(strFmt("@GLS220578", salesLineLoc.LineNum, CustInvoiceJour::findFromSalesId_IN(salesLineLoc.SalesId).InvoiceId));
    }

    return ret;
}

void flushRemainderCache()
{
    creditNoteReturnRemainder = null;
    creditNoteReturnRemainder = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Real);

    cwCreditNoteReturnRemainder = null;
    cwCreditNoteReturnRemainder = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Real);
}

        void upDateRemainderCache(CustInvoiceTrans _custInvoiceTrans)
        {
            if (!creditNoteReturnRemainder.exists(_custInvoiceTrans.RecId))
            {
                SalesQty qty;
                PdsCWInventQty cwQty;
                SalesLine localSalesLine = SalesLine::findInventTransId(_custInvoiceTrans.InventTransId);
                [qty, cwQty] = element.retrieveRemainReturnLotQtyAllCreditNote(_custInvoiceTrans, localSalesLine);

                GoodsInRouteInventTransIdDelivery_RU inventTransIdDelivery_RU;
                if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
                {
                    inventTransIdDelivery_RU = _custInvoiceTrans.custInvoiceTrans_RU().InventTransIdDelivery_RU;
                }
                if (inventTransIdDelivery_RU)
                {
                    qty = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(_custInvoiceTrans.ItemId,
																								_custInvoiceTrans.InventDimId,
																								-SalesLine::qtyDeliveryToCreditNote_RU(inventTransIdDelivery_RU),
																							    _custInvoiceTrans.inventTable().inventUnitId(),
																							    _custInvoiceTrans.SalesUnit,
																							    NoYes::Yes);

                    qty = localSalesLine.type().invoicedInTotal() - qty;
                }

                creditNoteReturnRemainder.insert(_custInvoiceTrans.RecId, qty);
                cwCreditNoteReturnRemainder.insert(_custInvoiceTrans.RecId, cwQty);
            }
        }

edit SalesInvoicedQty qty(boolean _set, CustInvoiceTrans _custInvoiceTrans, SalesQty _qty)
{
    Qty         qty = _custInvoiceTrans.Qty;
    boolean     set = _set;

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU])
        && _custInvoiceTrans.custInvoiceJour().custInvoiceJour_RU().InvoicePostingType_RU == SalesInvoicePostingType_RU::GoodsInRoute)
    {
        qty = _custInvoiceTrans.qtyDelivered_RU();
    }
    // </GEERU>

    if (element.mustValidateReturnItemQty())
    {
        if (SalesCopyingCWItemPartialReturnOrdersCorrectQtyToReturnFlight::instance().isEnabled() && _custInvoiceTrans.PdsCWQty && !set)
        {
            this.upDateRemainderCache(_custInvoiceTrans);
            qty = _custInvoiceTrans.Qty - creditNoteReturnRemainder.lookup(_custInvoiceTrans.RecId);
        }

        if (set)
        {
            //If a SO (with positive qty) is returned or credited, the returned/credited qty must not be zero or negative.
            //And the other way arround; If a SO (with negative qty) is returned/credited the qty must zero or positive.
            if ((qty >= 0 && _qty <= 0) || (qty <= 0 && _qty >= 0))
            {
                // Expected return quantity must not be zero
                set = checkFailed("@SYS105646");
            }
            else // A credited/returned (positive or negative)qty; A warning is launched if the qty from original SO is to be exceeded.
            {
                this.upDateRemainderCache(_custInvoiceTrans);
                set = this.validateReturnQty(element.updateQtyTotalInvoiced(_custInvoiceTrans),
                                             creditNoteReturnRemainder.lookup(_custInvoiceTrans.RecId),
                                             _qty);
            }
        }
        // <GBR>
        else if (BrazilParameters::isEnabled())
        {
            using (SalesLineRemainReturnLotQtyAllCreditNoteContext_BR context = SalesLineRemainReturnLotQtyAllCreditNoteContext_BR::construct())
            {
                context.parmCustInvoiceTransRecId(_custInvoiceTrans.RecId);
                _qty = SalesLine::remainReturnLotQtyAllCreditNote(_custInvoiceTrans.InventTransId);
            }
        }
        // </GBR>
    }

    qty = element.editQty(set,
                          _custInvoiceTrans,
                          _custInvoiceTrans.InvoiceId,
                          _custInvoiceTrans.LineNum,
                          _custInvoiceTrans.InvoiceDate,
                          qty,
                          _qty,
                          custInvoiceTrans_ds,
                          _custInvoiceTrans.SalesUnit);

    return qty;
}

void write()
{
}

int active()
{
    int             ret;

    ret = super();

    element.pdsCWEnableFields(custInvoiceTrans);

    // <GBR>
    if (BrazilParameters::isEnabled() && allowCallerUpdate)
    {
        custInvoiceTrans_ds.allowEdit(salesPurchCopy != SalesPurchCopy::VoidFiscalDocument_BR);
    }
    //</GBR>

    if (countryRegion_PL)
    {
        boolean allowEditInvoiceTrans;

        if (FeatureStateProvider::isFeatureEnabled(SalesEditLinesAdvanceInvoiceSettlementFeature_W::instance()))
        {
            allowEditInvoiceTrans = true;
        }
        else
        {
            allowEditInvoiceTrans = ! (salesPurchCopy == SalesPurchCopy::CreditNoteHeader &&
                element.advanceInvoiceSettled_W(custInvoiceTrans));
        }

        custInvoiceTrans_ds.allowEdit(allowEditInvoiceTrans);
    }

    return ret;
}

edit NoYes  copy(boolean            _set,
                 CustInvoiceTrans   _custInvoiceTrans,
                 NoYes              _copy)
{
    NoYes               ret = false;
    boolean             set = _set;
    Qty                 qty;
    // <GBR>
    Qty                 qtyLeftToReturn;
    // </GBR>

    PdsCWInventQty      cwQty;
    PdsCWInventQty      cwQtyLeftToReturnBR;

    curCustInvoiceTransRecId = _custInvoiceTrans.RecId;

    if (custInvoiceTrans.RecId == _custInvoiceTrans.RecId) // if it is the selected record
    {
        qty =  custInvoiceTrans_qty.realValue();
        cwQty = custInvoiceTrans_pdsCWQty.realValue();
    }
    else
    {
        element.findTmpFrmVirtual(tmpFrmVirtualLines, _custInvoiceTrans.TableId, _custInvoiceTrans.RecId);
        qty = tmpFrmVirtualLines ? tmpFrmVirtualLines.Qty : _custInvoiceTrans.Qty;
        cwQty = tmpFrmVirtualLines ? tmpFrmVirtualLines.PdsCWQty : _custInvoiceTrans.PdsCWQty;
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        qtyLeftToReturn = SalesLine::remainReturnLotQtyAllCreditNote(_custInvoiceTrans.InventTransId);
        if (PdsGlobal::pdsIsCWItem(_custInvoiceTrans.ItemId))
        {
            cwQtyLeftToReturnBR = PdsCatchWeight::cwQty(_custInvoiceTrans.ItemId, qtyLeftToReturn);
            cwQty = cwQty - cwQtyLeftToReturnBR;
        }
    }
    // </GBR>

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU])
        && _custInvoiceTrans.custInvoiceJour().custInvoiceJour_RU().InvoicePostingType_RU == SalesInvoicePostingType_RU::GoodsInRoute)
    {
        qty = _custInvoiceTrans.qtyDelivered_RU();
    }
    // </GEERU>

    if (element.mustValidateReturnItemQty())
    {
        if (_copy)
        {
            this.upDateRemainderCache(_custInvoiceTrans);
            if (PdsGlobal::pdsIsCWItem(_custInvoiceTrans.ItemId))
            {
                // validate according to Catch weight quantities
                set = this.validateReturnQty(element.pdsCWUpdateQtyTotalInvoiced(_custInvoiceTrans),
                                             cwCreditNoteReturnRemainder.lookup(_custInvoiceTrans.RecId),
                                             cwQty);
            }
            else
            {
                // <GEEU>
                if (countryRegion_RU || countryRegion_EU)
                {
                    set = this.validateCreditNoteExist();
                }
                else
                {
                    // </GEEU>
                    set = this.validateReturnQty(element.updateQtyTotalInvoiced(_custInvoiceTrans),
                                                creditNoteReturnRemainder.lookup(_custInvoiceTrans.RecId),
                                                qty);
                    // <GEEU>
                }
                // </GEEU>
            }
        }
    }

    ret = element.editCopy(set,
                           _custInvoiceTrans,
                           _custInvoiceTrans.InvoiceId,
                           _custInvoiceTrans.LineNum,
                           _custInvoiceTrans.InvoiceDate,
                           qty,
                           _copy,
                           cwQty);

    return ret;
}

public void init()
{
    QueryBuildDataSource qbdCustInvoiceTrans;

    creditNoteReturnRemainder     = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Real);

    cwCreditNoteReturnRemainder   = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Real);

    super();

    if (element.isSearchCriteriaVisible())
    {
        rangeItemId = this.query().dataSourceTable(tableNum(CustInvoiceTrans)).addRange(fieldNum(CustInvoiceTrans, ItemId));

        qbdCustInvoiceTrans = this.query().dataSourceTable(tableNum(CustInvoiceTrans));
        if (!qbdCustInvoiceTrans.findRange(fieldNum(CustInvoiceTrans, Qty)))
        {
            exclCreditNotes = qbdCustInvoiceTrans.addRange(fieldNum(CustInvoiceTrans, Qty));
        }

        exclCreditNotes.value(#positive);
    }

    this.cacheAddMethod(tableMethodStr(CustInvoiceTrans,pdsCWUnitId));
    custInvoiceTrans_ds.allowEdit(allowCallerUpdate);
}

