}

/// <summary>
/// Checks if the suggested return quantity exceeds the total invoiced quantity for the order line in question.
/// </summary>
/// <param name="_totalInvoiced">
/// A Qty type holding the total invoiced quantity for the order line.
/// </param>
/// <param name="_qtyAlreadyReturned">
/// A Qty type holding the already credited quantity for the order line.
/// </param>
/// <param name="_qtySelectForReturn">
/// A Qty type holding the quantity available for crediting for the order line.
/// </param>
/// <returns>
/// true if the quantity being returned isn't greater than what can be covered by the return lot; otherwise, false.
/// </returns>
boolean validateReturnQty(
    Qty _totalInvoiced,
    Qty _qtyAlreadyReturned,
    Qty _qtySelectForReturn
    )
{
    return SalesLine::validateReturnQty(_totalInvoiced, _qtyAlreadyReturned, _qtySelectForReturn);
}

public edit PdsCWInventQty pdsCWQty(
    boolean            _set,
    CustInvoiceTrans   _custInvoiceTrans,
    PdsCWInventQty     _pdsCWQty)
{
    PdsCWInventQty   pdsCWInventQty = _custInvoiceTrans.PdsCWQty;
    boolean          set = _set;

    if (element.mustValidateReturnItemQty())
    {
        this.upDateRemainderCache(_custInvoiceTrans);
        pdsCWInventQty = _custInvoiceTrans.PdsCWQty - cwCreditNoteReturnRemainder.lookup(_custInvoiceTrans.RecId);

        if (_set)
        {
            //If a SO (with positive qty) is returned or credited, the returned/credited qty must not be zero or negative.
            //And the other way arround; If a SO (with negative qty) is returned/credited the qty must zero or positive.
            if ((pdsCWInventQty >= 0 && _pdsCWQty <= 0)
                || (pdsCWInventQty <= 0 && _pdsCWQty >= 0))
            {
                set = false;
                // Expected return quantity must not be zero
                error("@SYS105646");
            }
            // A credited/returned (positive or negative)qty; The qty from original SO can not be exceeded.
            else if ((pdsCWInventQty <= 0 &&_pdsCWQty < pdsCWInventQty)
                || (pdsCWInventQty >= 0 && _pdsCWQty > pdsCWInventQty) )
            {
                set = false;
                // The quantity being returned is greater than what can be covered by the return lot.
                error("@SYS53004");
            }
        }
    }

    return element.pdsCWEditQty(set,
        _custInvoiceTrans,
        _custInvoiceTrans.InvoiceId,
        _custInvoiceTrans.LineNum,
        _custInvoiceTrans.InvoiceDate,
        pdsCWInventQty,
        _pdsCWQty,
        custInvoiceTrans_ds,
        custInvoiceTrans.ItemId,
        _custInvoiceTrans.SalesUnit);
}

public void init()
{
    super();

    this.cacheAddMethod(tableMethodStr(CustPackingSlipTrans,pdsCWUnitId));
