void init()
{
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildDataSource qbdsCustInvoiceTrans;
    QueryBuildRange      qbrQty;
    boolean              isCreditNoteHeaderOrLine = salesPurchCopy == SalesPurchCopy::CreditNoteLines ||
                                                    salesPurchCopy == SalesPurchCopy::CreditNoteHeader
                                                    // <GBR>
                                                    || salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR
                                                    // </GBR>
                                                    ;

    super();
    queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.clearDynalinks();

    if (salesPurchCopy == SalesPurchCopy::CopyJournalLines || salesPurchCopy == SalesPurchCopy::CopyJournalHeader)
    {
        onlyCustInvoiceJour_SalesId = queryBuildDataSource.addRange(fieldNum(CustInvoiceJour, SalesId));
    }

    if (isCreditNoteHeaderOrLine)
    {
        onlyInvoiceAccount = queryBuildDataSource.addRange(fieldNum(CustInvoiceJour, InvoiceAccount));

        if (element.isSearchCriteriaVisible())
        {
            rangeSalesId = queryBuildDataSource.addRange(fieldNum(CustInvoiceJour, SalesId));
            rangeInvoiceDate = queryBuildDataSource.addRange(fieldNum(CustInvoiceJour, InvoiceDate));
        }
    }

    if (calledFromCustomerService && isMergedCustomerAccount)
    {
        onlyOrderAccount = queryBuildDataSource.addRange(fieldNum(CustInvoiceJour, OrderAccount));
    }

    onlyOrder = this.query().dataSourceTable(tableNum(CustInvoiceJour)).addRange(fieldNum(CustInvoiceJour, RefNum));

    // <GEEU>
    if (element.isSearchCriteriaVisible() || (isCreditNoteHeaderOrLine && !(countryRegion_RU || countryRegion_EU)))
    // </GEEU>
    {
        qbdsCustInvoiceTrans = queryBuildDataSource.addDataSource(tableNum(CustInvoiceTrans));
        qbdsCustInvoiceTrans.addLink(fieldNum(CustInvoiceJour, SalesId),fieldNum(CustInvoiceTrans, SalesId));
        qbdsCustInvoiceTrans.joinMode(JoinMode::ExistsJoin);
        qbrQty = qbdsCustInvoiceTrans.addRange(fieldNum(CustInvoiceTrans, Qty));
        // <GBR>
        if (!BrazilParameters::isEnabled())
        {
            // </GBR>
            qbrQty.value(#positive);
            // <GBR>
        }
        // </GBR>
    }

    // <GBR>
    if (BrazilParameters::isEnabled() && salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR)
    {
        queryBuildDataSource = this.query().dataSourceTable(tableNum(CustInvoiceJour)).addDataSource(tableNum(FiscalDocument_BR));
        queryBuildDataSource.addLink(fieldNum(CustInvoiceJour, RecId), fieldNum(FiscalDocument_BR, RefRecId));
        queryBuildDataSource.addLink(fieldNum(CustInvoiceJour, TableId), fieldNum(FiscalDocument_BR, RefTableId));
        queryBuildDataSource.addRange(fieldNum(FiscalDocument_BR, Status)).value(strFmt('%1,%2,%3,%4,%5',
            queryValue(FiscalDocumentStatus_BR::Approved),
            queryValue(FiscalDocumentStatus_BR::Created),
            queryValue(FiscalDocumentStatus_BR::Rejected),
            queryValue(FiscalDocumentStatus_BR::RejectedNoFix),
            queryValue(FiscalDocumentStatus_BR::Denied)));
        queryBuildDataSource.joinMode(JoinMode::InnerJoin);
    }
    // </GBR>
    custInvoiceJour_ds.allowEdit(allowCallerUpdate);
}

void executeQuery()
{
    if (salesPurchCopy == SalesPurchCopy::CopyJournalLines ||
        salesPurchCopy == SalesPurchCopy::CopyJournalHeader)
    {
        onlyCustInvoiceJour_SalesId.value(queryValue(callerSalesTable.SalesId));
    }

    if (salesPurchCopy == SalesPurchCopy::CreditNoteLines ||
        salesPurchCopy == SalesPurchCopy::CreditNoteHeader
        // <GBR>
        || salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR
        // </GBR>
        )
    {
        if (calledFromCustomerService && isMergedCustomerAccount)
        {
            onlyOrderAccount.value(customerQueryStr);
        }
        else
        {
            onlyInvoiceAccount.value(queryValue(callerSalesTable.InvoiceAccount));
        }
    }

    onlyOrder.value(enum2Value(RefNum::SalesOrder));

    super();
}

int active()
{
    int ret;

    ret = super();

    custInvoiceTrans_ds.flushRemainderCache();
    curCustInvoiceTransRecId = CustInvoiceTrans.RecId;

    // <GIN>
    if (TaxParameters::checkTaxParameters_IN())
    {
        if (SalesTable::find(custInvoiceJour.SalesId).CustomsExportOrder_IN)
        {
            taxPerOriginalInvoice_IN.value(0);
            taxPerOriginalInvoice_IN.enabled(false);
        }
        else
        {
            taxPerOriginalInvoice_IN.enabled(true);
        }
    }
    // </GIN>

    return ret;
}

