void init()
{
    QueryBuildDataSource queryBuildDataSource;

    // <GEERU>
    QueryBuildDataSource qbds;
    // </GEERU>
    super();
    // <GEERU>
    if (countryRegion_RU)
    {
        qbds = this.query().dataSourceTable(tableNum(ProjInvoiceJour)).addDataSource(tableNum(ProjInvoiceItem));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.relations(true);

        if (salesPurchCopy == SalesPurchCopy::CopyJournalLines ||
            salesPurchCopy == SalesPurchCopy::CopyJournalHeader)
        {
            qbds.addRange(fieldNum(ProjInvoiceItem, SalesId)).value(queryValue(callerSalesTable.SalesId));
        }

        qbds.addRange(fieldNum(ProjInvoiceItem,ItemType)).value(queryValue(ProjItemType::SalesOrder));
    }
    // </GEERU>

    queryBuildDataSource = this.query().dataSourceTable(this.table());
    queryBuildDataSource.clearDynalinks();

    if (salesPurchCopy == SalesPurchCopy::CopyJournalLines ||
        salesPurchCopy == SalesPurchCopy::CopyJournalHeader ||
        salesPurchCopy == SalesPurchCopy::CreditNoteLines ||
        salesPurchCopy == SalesPurchCopy::CreditNoteHeader
        // <GBR>
        || salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR
        // </GBR>
        )
    {
        onlyProjInvoiceAccount = queryBuildDataSource.addRange(fieldNum(ProjInvoiceJour, InvoiceAccount));

        // <GEEU>
        // This code was added in the SYP layer but is too restrictive in the invoices selected.
        // The purpose was to exclude invoices with "item requirements" but it excluded all
        // invoices when creating a credit note based on a new sales order.  "Item requirements" invoices
        // will appear but their lines will be excluded by the the range:
        //      ProjInvoiceItem.itemType == ProjItemType::SalesOrder
        // Item requirements based lines have an itemType == ProjItemType::Project
        // so the "extra" invoice headers that may appear will be harmless.
        // </GEEU>
    }
    projInvoiceJour_ds.allowEdit(allowCallerUpdate);
}

void executeQuery()
{
    if (salesPurchCopy == SalesPurchCopy::CreditNoteLines ||
        salesPurchCopy == SalesPurchCopy::CreditNoteHeader ||
        salesPurchCopy == SalesPurchCopy::CopyJournalLines ||
        salesPurchCopy == SalesPurchCopy::CopyJournalHeader
        // <GBR>
        || salesPurchCopy == SalesPurchCopy::VoidFiscalDocument_BR
        // </GBR>
        )
    {
        onlyProjInvoiceAccount.value(queryValue(callerSalesTable.InvoiceAccount));
    }

    super();
}

