    }

    projInvoiceItem_ds.query().dataSourceTable(tableNum(ProjInvoiceItem)).addRange(fieldNum(ProjInvoiceItem,ItemType)).value(queryValue(ProjItemType::SalesOrder));
    projInvoiceItem_ds.allowEdit(allowCallerUpdate);
}

