edit NoYes  copy(boolean            _set,
                 ProjInvoiceItem    _projInvoiceItem,
                 NoYes              _copy)
{
    NoYes               ret = false;
    boolean set = _set;

    if (ProjCreditNoteInitiatedViaSalesOrderCreditNoteFlight::instance().isEnabled() && _copy)
    {
        // <GEEU>
        if (countryRegion_RU || countryRegion_EU)
        {
            set = this.validateCreditNoteExist(_projInvoiceItem);
        }
    }

    ret = element.editCopy(set,
                        _projInvoiceItem,
                        _projInvoiceItem.ProjInvoiceId,
                         0,
                        _projInvoiceItem.InvoiceDate,
                        _projInvoiceItem.Qty,
                        _copy);

    return ret;
}

void executeQuery()
{
    if (salesPurchCopy == SalesPurchCopy::CopyJournalLines ||
        salesPurchCopy == SalesPurchCopy::CopyJournalHeader)
    {
        onlyProjInvoiceItem_SalesId.value(queryValue(callerSalesTable.SalesId));
    }

    super();
}

private boolean validateCreditNoteExist(ProjInvoiceItem _projInvoiceItem)
{
    boolean ret = true;
    SalesLine salesLineLoc;
    SalesTable salesTableLoc;

    select firstonly SalesId, RecId from salesLineLoc
        where salesLineLoc.RefReturnInvoiceTrans_W == _projInvoiceItem.RecId
    exists join salesTableLoc
        where salesTableLoc.SalesId == salesLineLoc.SalesId
            && salesTableLoc.ReturnStatus != ReturnStatusHeader::Canceled;

    if (salesLineLoc.RecId != 0)
    {
        ProjInvoiceItem projInvoiceItemFromCreditNote;

        select firstonly ProjInvoiceId from projInvoiceItemFromCreditNote
            where projInvoiceItemFromCreditNote.RefReturnInvoiceTrans_W == _projInvoiceItem.RecId;

        if (projInvoiceItemFromCreditNote)
        {
            ret = checkFailed(strFmt("@Proj:ProjItemAlreadyCredited", projInvoiceItemFromCreditNote.ProjInvoiceId));
        }
        else
        {
            ret = checkFailed(strFmt("@Proj:ProjItemAlreadyIncludedInCorrectiveSalesOrder", salesLineLoc.SalesId));
        }
    }

    return ret;
}

