{
    ReasonTable::jumpRef(ReasonCodeAccountTypeAll::AllValues);
}

public void lookup()
{
    ReasonFormTable::reasonCodeLookup(
        this,
        LedgerJournalACType::Cust,
        LedgerJournalACType::Cust,
        false,
        false,
        LedgerJournalACType::Cust,
        true);
}

void lookup()
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;
    SysTableLookup          sysTableLookup;

    // Create an instance of SysTableLookup with the form control passed in
    sysTableLookup = SysTableLookup::newParameters(tableNum(CustInvoiceJour), this);

    // Add the fields to be shown in the lookup form
    sysTableLookup.addLookupfield(fieldNum(CustInvoiceJour, SalesId));
    sysTableLookup.addLookupfield(fieldNum(CustInvoiceJour, InvoiceId));
    sysTableLookup.addLookupfield(fieldNum(CustInvoiceJour, InvoiceDate));

    // Create the query datasource
    queryBuildDataSource = query.addDataSource(tableNum(CustInvoiceJour));
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(CustInvoiceJour, InvoiceAccount));
    queryBuildRange.value(callerSalesTable.InvoiceAccount);

    // Add the query to the lookup form
    sysTableLookup.parmQuery(query);

    // Perform lookup
    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    rangeSalesId.value(searchSalesId.text());
    custInvoiceJour_ds.executeQuery();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (!filterItemqbd)
    {
        filterItemqbd = custInvoiceJour_ds.query().dataSourceTable(tableNum(CustInvoiceJour)).addDataSource(tableNum(CustInvoiceTrans));
        filterItemqbd.relations(true);
        filterItemqbd.joinMode(JoinMode::ExistsJoin);
        rangeItemId2 = filterItemqbd.addRange(fieldNum(CustInvoiceTrans, ItemId));
    }

    rangeItemId.value(searchItemId.text());
    rangeItemId2.value(searchItemId.text());

    custInvoiceTrans_ds.executeQuery();
    custInvoiceJour_ds.executeQuery();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.filterDateModified(searchFromDate.dateValue(), searchToDate.dateValue());

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.filterDateModified(searchFromDate.dateValue(), searchToDate.dateValue());

    return ret;
}

/// <summary>
/// Lookup for the return reason code.
/// </summary>
public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource qbds;

    SysTableLookup sysTableLookup = sysTableLookup::newParameters(tableNum(ReturnReasonCode), this);
    qbds= query.addDataSource(tableNum(ReturnReasonCode));

    sysTableLookup.addLookupfield(fieldNum(ReturnReasonCode, ReasonCodeId));
    sysTableLookup.addLookupfield(fieldNum(ReturnReasonCode, Description));
    sysTableLookup.addLookupfield(fieldNum(ReturnReasonCode, ReasonCodeGroupId));

    qbds.fields().addField(fieldNum(ReturnReasonCode, ReasonCodeId));
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

/// <summary>
/// Validate If user enter invalid reason codes.
/// </summary>
/// <returns>Returns true if the reason code entered by the user is valid; otherwise, returns false.</returns>
public boolean validate()
{
    boolean ret = true;

    if (RetailSalesCopyingFormReturnReasonCodeValidationFlight::instance().isEnabled())
    {
        str result = EditReturnReasonCode.valueStr();

        if (result && !ReturnReasonCode::exist(result))
        {
            ret = checkFailed(strFmt("@REX4161301", result, fieldStr(ReturnReasonCode, ReasonCodeId), tableStr(ReturnReasonCode)));
        }
    }

    return ret;
}

