void init()
{
    SalesTable           salesTableLocal;
    SalesAgreementHeader salesAgreementHeader;
    MCROrderParameters   mcrOrderParameters = MCROrderParameters::find();
    #ISOCountryRegionCodes

    // has to be before super to be used in methods such as FormDataSource.init
    countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    isIN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);

    enableDirectedSelling = RetailMCRChannelTable::mcrEnableDirectedSellingFindByUser();

    mcrCallCenterEnabled = isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter));

    super();
    boolean isCallCenterOrder = MCRChannelUser::find() ? true : false;
    SalesTable_MCRExpedite.visible(isCallCenterOrder);

    if (!element.args().caller())
    {
        throw error("@SYS22539");
    }

    salesTableForm          = element.args().caller();
    salesCreateOrderForm    = SalesCreateOrderForm::construct(salesTableForm.defaultSalesType(), salesTableForm.project());

    if (salesTableForm.replacementOrder()
    &&  salesTableForm.returnItemNum())
    {
        select firstonly salesTableLocal
               where salesTableLocal.ReturnItemNum == salesTableForm.returnItemNum()
               &&    salesTableLocal.SalesType     == SalesType::ReturnItem;

        if (salesTableLocal.MatchingAgreement)
        {
            salesAgreementHeader = SalesAgreementHeader::find(salesTableLocal.MatchingAgreement);
            if (salesAgreementHeader.RecId)
            {
                salesTable.initFromSalesAgreementHeader(salesAgreementHeader);
                salesAgreementId = salesAgreementHeader.SalesNumberSequence;
            }
        }

        if (salesTableLocal
        &&  salesTableLocal.isInterCompanyOrder()
        &&  salesTableLocal.InterCompanyDirectDeliveryOrig)
        {
            error("@SYS120349");
            element.close();
        }
    }

    if (salesTableForm.parmReturnStatus() == ReturnStatusHeader::Created)
    {
        // Create return order
        element.design().caption("@SYS104102");
    }

    TaxVATNumTable::enableLookupVATNum(salesTable_VATNum);

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.enableFieldsActive_IN();
    }

    mcrSourceCode.visible(enableDirectedSelling);
    if (enableDirectedSelling)
    {
        mcrSourceCode.mandatory(mcrOrderParameters.mcrSourceCodeRequired);
    }

    bankLCExportFeatureChecker  = BankLCExportFeatureChecker::construct();
    bankLGFeatureChecker        = BankLGFeatureChecker::construct();
    salesTable_DS.object(fieldNum(SalesTable, BankDocumentType)).visible(
        bankLCExportFeatureChecker.checkFeatureEnabled()
            || bankLGFeatureChecker.checkFeatureEnabled());

    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    if (countryRegion_RU)
    {
        editSalesAgreementId.visible(false);
    }

    boolean projOperationsIntegrationFeatureIsNotEnabled = !ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();
    SalesTable_ds.object(fieldNum(SalesTable, ProjId)).visible(projOperationsIntegrationFeatureIsNotEnabled);
}

