void mcrRunCustomerSearch()
{
    AccountNum custSearchAccount;
    utcdatetime timeNow = DateTimeUtil::utcNow();

    // If there is a search string
    if (mcrSearchText.text() && timeNow > DateTimeUtil::addSeconds(formCloseTime, 1))
    {
        custSearchAccount = MCRCustSearch::searchDisplayForm(mcrSearchText.text(), mcrCustSearchType.selectionAsMCRCustSearchType(), true, element);
        formCloseTime = DateTimeUtil::utcNow();

        // If a customer was found
        if (custSearchAccount != "")
        {
            select firstonly custTable where custTable.AccountNum == custSearchAccount;
            custSearchAccount = strRem(custSearchAccount, ",");
            salesTable.CustAccount = custSearchAccount;
            salesTable_CustAccount.text(custSearchAccount);
            salesTable_CustAccount.modified();

            // If a customer was returned, set focus on source code.
            element.mcrSetSourceCodeFocus();
        }
    }
}

